import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

function Competition() {
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [finishedTimes, setFinishedTimes] = useState({});


  useEffect(() => {
    document.title = 'Kilpailu';
    const socket = io('http://localhost:8000');

    socket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    socket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    socket.on('selectedGroupUpdate', (updatedGroupData) => {
      console.log('Received updated group data:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);

      localStorage.setItem('selectedGroup', updatedGroupData);
    });

    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
    }

    

    return () => {
      if (socket.connected) {
        socket.disconnect();
      }
    };
  }, []);

  return (
    <div>
      <h1>Competition</h1>
      {selectedGroup ? (
        <div>
          <h2>{selectedGroup.groupInfo}</h2>
          <h3>Selected Task: {selectedGroup.selectedTask ? selectedGroup.selectedTask.tehtävä_nimi : 'None'}</h3>
          <p>Max Aika: {selectedGroup.selectedTask ? selectedGroup.selectedTask.max_aika : 'N/A'}</p>

          <ul>
            {selectedGroup.teams.map((team) => (
              <li key={team.joukkue_id}>
                <h3>{team.joukkue_nimi}</h3>
                <p>Members: {team.jäsenet}</p>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No selected group available.</p>
      )}
    </div>
  );
}

export default Competition;
