import React, { useState } from 'react';

function Register() {
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    jäsenet: '',
    kaupunki: '',
    osallistumis_nro: 0,
    semifinalist: 0,
    finalist: 0,
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch('http://localhost:3001/api/joukkueet', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        // Handle the response data, e.g., show a success message or reset the form.
        console.log('Joukkue created:', data);
        // You can reset the form or show a success message here.
      })
      .catch((error) => {
        // Handle errors, e.g., show an error message.
        console.error('Error creating joukkue:', error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'osallistumis_nro' || name === 'semifinalist' || name === 'finalist' ? parseInt(value) : value,
    });
  };

  

  return (
    <div>
      <h2>Register a New Joukkue</h2>
      <form onSubmit={handleSubmit}>
        <label>Joukkue Nimi:
          <input type="text" name="joukkue_nimi" onChange={handleInputChange} value={formData.joukkue_nimi} />
        </label>
        <br />
        <label>Jäsenet:
          <input type="text" name="jäsenet" onChange={handleInputChange} value={formData.jäsenet} />
        </label>
        <br />
        <label>Kaupunki:
          <input type="text" name="kaupunki" onChange={handleInputChange} value={formData.kaupunki} />
        </label>
        <br />
        <label>Osallistumis Numero:
          <input type="text" name="osallistumis_nro" onChange={handleInputChange} value={formData.osallistumis_nro} />
        </label>
        <br />
        <label>Semifinalist:
          <input type="checkbox" name="semifinalist" onChange={handleInputChange} checked={formData.semifinalist} />
        </label>
        <br />
        <label>Finalist:
          <input type="checkbox" name="finalist" onChange={handleInputChange} checked={formData.finalist} />
        </label>
        <br />
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default Register;
