// AjastinKello.js

import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

const AjastinKello = ({ teamId, task }) => {
  const [elapsedTime, setElapsedTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const intervalIdRef = React.useRef(null);
  
  const newSocket = io('http://localhost:8000');

  newSocket.on('connect', () => {
    console.log('WebSocket connection is open.');
  });

  newSocket.on('disconnect', () => {
    console.log('WebSocket connection is closed.');
  });

  useEffect(() => {
    return () => {
      clearInterval(intervalIdRef.current);
    };
  }, []);

  useEffect(() => {
    if (isRunning) {
      const intervalId = setInterval(() => {
        setElapsedTime((prevElapsedTime) => prevElapsedTime + 1);
      }, 10);

      intervalIdRef.current = intervalId;
    } else {
      clearInterval(intervalIdRef.current);

      // If the timer is stopped, emit the finished time
      const finishedTime = new Date().toISOString();
      socket.emit('finishedTime', {
        teamId,
        taskName: task.tehtävä_nimi,
        finishedTime,
      });
    }

    return () => {
      clearInterval(intervalIdRef.current);
    };
  }, [isRunning, teamId, task.tehtävä_nimi, socket]);

  const startTimer = () => {
    setIsRunning(true);
  };

  const stopTimer = () => {
    setIsRunning(false);
  };

  const resetTimer = () => {
    setElapsedTime(0);
    clearInterval(intervalIdRef.current);
    setIsRunning(false);
  };

  const formatTime = (timeInMilliseconds) => {
    const minutes = Math.floor(timeInMilliseconds / (60 * 100));
    const seconds = Math.floor((timeInMilliseconds % (60 * 100)) / 100);
    const milliseconds = timeInMilliseconds % 100;

    return `${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}:${milliseconds < 10 ? '0' : ''}${milliseconds}`;
  };

  return (
    <div>
      <h3>{task.tehtävä_nimi}</h3>
      <div className="timer">
        <span>{formatTime(elapsedTime * 10)}</span>
      </div>
      <div className="controls">
        <button onClick={startTimer} disabled={isRunning}>
          Start
        </button>
        <button onClick={stopTimer} disabled={!isRunning}>
          Stop
        </button>
        <button onClick={resetTimer}>Reset</button>
      </div>
    </div>
  );
};

export default AjastinKello;
