import React, { useState, useEffect } from 'react';
import axios from 'axios';
import useWebSocket from './useWebSocket';
import { sendSelectedTaskData } from './WebSocketUtils';

function TasksSelector({ onSelectTask }) {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState({});
  const socket = useWebSocket();

  useEffect(() => {
    axios.get('http://localhost:3001/api/tehtavat')
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });
  }, []);

  const handleTaskSelect = async (task) => {
    console.log('Selected Task:', task);
    if (task && task.id) {
      const selectedTaskData = {
        id: task.id,
        tehtävä_nimi: task.tehtävä_nimi,
        max_aika: task.max_aika,
      };

      try {
        await sendSelectedTaskData(selectedTaskData);
        setSelectedTask(selectedTaskData);
      } catch (error) {
        console.error('Error sending selected task data:', error.message);
      }
    } else {
      console.error('Invalid task object:', task);
    }
  };

  return (
    <div>
      <h2>Task Selector</h2>
      <select
        value={selectedTask ? selectedTask.id : ''}
        onChange={(e) => {
          const taskId = e.target.value;
          const selected = tasks.find((task) => task.id === parseInt(taskId, 10));

          if (selected) {
            handleTaskSelect(selected);
          } else {
            console.error('Invalid task selected:', taskId);
          }
        }}
      >
        <option key="default" value="0">Select a task</option>
        {tasks.map((task) => (
          <option key={task.id} value={task.id}>
            {task.tehtävä_nimi}
          </option>
        ))}
      </select>
    </div>
  );
}

export default TasksSelector;
