import React, { useState } from 'react';
import axios from 'axios';

function Register() {
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    jäsenet: '',
    kaupunki: '',
    osallistumis_nro: '',
    semifinalist: false,
    finalist: false,
  });

  const { joukkue_nimi = '', jäsenet = '', kaupunki = '', osallistumis_nro = 0, semifinalist = false, finalist = false } = joukkue;


  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post('http://localhost:3001/api/joukkueet', formData); // Correct URL
      alert('Data submitted successfully');
    } catch (error) {
      console.error('Error submitting data:', error);
      alert('An error occurred while submitting data');
    }
  };

  return (
    <div>
      <h1>Register</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="joukkue_nimi">Joukkue Nimi:</label>
          <input
            type="text"
            name="joukkue_nimi"
            value={formData.joukkue_nimi}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="jäsenet">Jäsenet:</label>
          <input
            type="text"
            name="jäsenet"
            value={formData.jäsenet}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="kaupunki">Kaupunki:</label>
          <input
            type="text"
            name="kaupunki"
            value={formData.kaupunki}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="osallistumis_nro">Osallistumis Nro:</label>
          <input
            type="number"
            name="osallistumis_nro"
            value={formData.osallistumis_nro}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>
            Semifinalist:
            <input
              type="checkbox"
              name="semifinalist"
              checked={formData.semifinalist}
              onChange={handleChange}
            />
          </label>
        </div>
        <div>
          <label>
            Finalist:
            <input
              type="checkbox"
              name="finalist"
              checked={formData.finalist}
              onChange={handleChange}
            />
          </label>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Register;
