import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';

function Ajastin() {
  const { teamId } = useParams();
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [milliseconds, setMilliseconds] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    document.title = `Team ${teamId}`;
    const newSocket = io('http://localhost:8000');

    newSocket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    newSocket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    newSocket.on('selectedGroupUpdate', (updatedGroupData) => {
      console.log('Received updated group data:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);

      localStorage.setItem('selectedGroup', updatedGroupData);
    });

    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
    }

    setSocket(newSocket);

    return () => {
      if (newSocket.connected) {
        newSocket.disconnect();
      }
    };
  }, [teamId]);

  useEffect(() => {
    let timer;
    if (isRunning) {
      timer = setInterval(() => {
        if (milliseconds === 99) {
          if (seconds === 59) {
            setMinutes((prevMinutes) => prevMinutes + 1);
            setSeconds(0);
          } else {
            setSeconds((prevSeconds) => prevSeconds + 1);
          }
          setMilliseconds(0);
        } else {
          setMilliseconds((prevMilliseconds) => prevMilliseconds + 1);
        }
      }, 10);
    }

    return () => {
      clearInterval(timer);

      // Broadcast finished time to the socket when the timer stops
      if (socket) {
        const finishedTime = `${minutes}:${seconds}:${milliseconds}`;
        const finishedTimeData = {
          teamId,
          finishedTime,
        };
        socket.emit('finishedTime', JSON.stringify(finishedTimeData));
      }
    };
  }, [isRunning, minutes, seconds, milliseconds, socket, teamId]);

  const startTimer = () => {
    setIsRunning(true);
  };

  const stopTimer = () => {
    setIsRunning(false);
  };

  const resetTimer = () => {
    setIsRunning(false);
    setMinutes(0);
    setSeconds(0);
    setMilliseconds(0);
  };

  return (
    <div>
      <h2>{`Team ${teamId} Stopwatch`}</h2>
      {selectedGroup && (
        <div>
          <h3>{`Selected Task: ${selectedGroup.selectedTask.tehtävä_nimi}`}</h3>
          <p>Max Aika: {selectedGroup.selectedTask ? selectedGroup.selectedTask.max_aika : 'N/A'}</p>
        </div>
      )}
      <div className="timer">
        <span>{minutes < 10 ? `0${minutes}` : minutes}</span>:
        <span>{seconds < 10 ? `0${seconds}` : seconds}</span>:
        <span>{milliseconds < 10 ? `0${milliseconds}` : milliseconds}</span>
      </div>
      <div className="controls">
        <button onClick={startTimer}>Start</button>
        <button onClick={stopTimer}>Stop</button>
        <button onClick={resetTimer}>Reset</button>
      </div>
    </div>
  );
}

export default Ajastin;
