import React, { useState, useEffect } from 'react';
import TasksSelector from './TasksSelector';  // Make sure to adjust the path accordingly
import { io } from 'socket.io-client';

function CompetitionAlkuerä() {
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [selectedTask, setSelectedTask] = useState(null);

  const handleGroupSelect = (group) => {
    // Assuming group has a property named 'groupInfo'
    console.log('Selected Group:', group.groupInfo);
    setSelectedGroup(group);
  };

  const handleTaskSelect = (task) => {
    console.log('Selected Task:', task);
    setSelectedTask(task);
  };

  const handleSendData = () => {
    if (selectedGroup && selectedTask) {
      const dataToSend = {
        selectedGroup,
        selectedTask,
      };
      const socket = io('http://localhost:8000');
      socket.emit('selectedDataUpdate', dataToSend);
    } else {
      console.error('Both group and task must be selected.');
    }
  };

  return (
    <div>
      <h1>CompetitionAlkuerä</h1>
      
      {/* Group Selector */}
      <TasksSelector onSelectGroup={handleGroupSelect} />

      {/* Task Selector */}
      <TasksSelector onSelectTask={handleTaskSelect} />

      <button onClick={handleSendData}>Send Data</button>

      {selectedGroup && selectedTask ? (
        <div>
          <h2>{selectedGroup.groupInfo}</h2>
          <p>Selected Task: {selectedTask.tehtävä_nimi}</p>
        </div>
      ) : (
        <p>No selected group or task available.</p>
      )}
    </div>
  );
}

export default CompetitionAlkuerä;
