import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/Joukkueet.css';

function Joukkueet(newlyAddedTeam) {
    const [teams, setTeams] = useState([]);
  
    useEffect(() => {
      // Fetch teams from the server
      axios
        .get('http://localhost:3001/api/joukkueet')
        .then((response) => {
          setTeams(response.data);
        })
        .catch((error) => {
          console.error('Error fetching teams:', error);
        });
    }, []);
  
    return (
      <div>
        <h2 className="team-heading">Joukkueet</h2>
        {/* Display the newly added team data */}
        {newlyAddedTeam && (
          <div className="newly-added-team">
            <h3>Newly Added Team:</h3>
            <p>Joukkue Nimi: {newlyAddedTeam.joukkue_nimi}</p>
            <p>Kaupunki: {newlyAddedTeam.kaupunki}</p>
            <p>Jäsenet: {newlyAddedTeam.jäsenet}</p>
          </div>
        )}
        <ul className="team-list">

          {teams.map((team) => (
            <li key={team.joukkue_id} className="team-card">
              {team.joukkue_nimi}
              <div className="team-info">
                <p>Kaupunki: {team.kaupunki}</p>
                <p>Jäsenet: {team.jäsenet}</p>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
    }

export default Joukkueet;
