import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import '../css/Competition.css';

function Competition() {
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [finishedTimes, setFinishedTimes] = useState({});
  const [updatedGroupData, setUpdatedGroupData] = useState(null);

  useEffect(() => {
    const socket = io('http://localhost:8000');

    socket.on('selectedGroupUpdate', (updatedGroupData) => {
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);
      setUpdatedGroupData(parsedData);
      localStorage.setItem('selectedGroup', JSON.stringify(parsedData));
    });

    socket.on('groupDataUpdate', (updatedGroupData) => {
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);
      setUpdatedGroupData(parsedData);
      localStorage.setItem('selectedGroup', JSON.stringify(parsedData));
    });

    socket.on('finishedTimeUpdate', (finishedTimeData) => {
      console.log('Raw server response:', finishedTimeData);
    
      try {
        const parsedData = JSON.parse(finishedTimeData);
    
        if (parsedData && parsedData.teamId && parsedData.finishedTime && parsedData.era) {
          setFinishedTimes((prevFinishedTimes) => {
            const updatedFinishedTimes = {
              ...prevFinishedTimes,
              [parsedData.teamId]: {
                ...prevFinishedTimes[parsedData.teamId],
                [parsedData.era]: {
                  finishedTime: parsedData.finishedTime,
                  era: parsedData.era,
                  tehtava: parsedData.taskName,
                },
              },
            };
            
    
            localStorage.setItem('finishedTimes', JSON.stringify(updatedFinishedTimes));
    
            console.log('Updated finishedTimes:', updatedFinishedTimes);
            return updatedFinishedTimes;
          });
        } else {
          console.error('Invalid finished time data:', parsedData);
        }
      } catch (error) {
        console.error('Error handling finished time data:', error);
      }
    });
    
    
    
    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
      setUpdatedGroupData(parsedData);
    }

    return () => {
      if (socket.connected) {
        socket.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    const storedFinishedTimes = localStorage.getItem('finishedTimes');
    if (storedFinishedTimes) {
      const parsedFinishedTimes = JSON.parse(storedFinishedTimes);
      setFinishedTimes(parsedFinishedTimes);
      console.log('Parsed Finished Times:', parsedFinishedTimes);
    }
  }, []);

  return (
    <div>
      <h1>Competition</h1>
      {updatedGroupData ? (
        <div>
          <h2>{updatedGroupData.groupInfo}</h2>
          <h3>Selected Tasks: {updatedGroupData.selectedTasks.map((task) => `${task.tehtävä_nimi} (${task.max_aika})`).join(', ')}</h3>
          <ul>
          {updatedGroupData.teams.map((team) => (
  <li key={team.joukkue_id}>
    <h3>{team.joukkue_nimi}</h3>
    {updatedGroupData.selectedTasks.map((task) => {
      const teamData = finishedTimes[team.joukkue_id];
      const finishedTimeData = teamData?.[task.era]?.tehtava;
      con
      console.log('finishedTimeData:', finishedTimeData); // Add this line to log finishedTimeData

      return (
        <p key={task.tehtävä_nimi}>
          {task.tehtävä_nimi}: {finishedTimeData ? finishedTimeData.finishedTime : 'Not completed'}
        </p>
      );
    })}
    <p>Members: {team.jäsenet}</p>
  </li>
))}






          </ul>
        </div>
      ) : (
        <p>No selected group available.</p>
      )}
    </div>
  );
}

export default Competition;
