import React, { useState } from 'react';
import axios from 'axios';

function Register() {
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    j채senet: '',
    kaupunki: '',
    osallistumis_nro: null, // Initialize as null
    semifinalist: 0,
    finalist: 0,
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    // Send the form data to your API
    axios
      .post('http://localhost:3001/api/joukkueet', formData)
      .then((response) => {
        // Handle the response data, e.g., show a success message or reset the form.
        console.log('Joukkue created:', response.data);
        // You can reset the form or show a success message here.
      })
      .catch((error) => {
        // Handle errors, e.g., show an error message.
        console.error('Error creating joukkue:', error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'osallistumis_nro' || name === 'semifinalist' || name === 'finalist' ? parseInt(value) : value,
    });
  };

  return (
    <div>
      <h2>Register Joukkue</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Joukkue Nimi:
          <input type="text" name="joukkue_nimi" value={formData.joukkue_nimi} onChange={handleInputChange} />
        </label>
        <label>
          Osallistumis Nro:
          <input type="number" name="osallistumis_nro" value={formData.osallistumis_nro} onChange={handleInputChange} />
        </label>
        {/* Include other form fields for j채senet, kaupunki, semifinalist, and finalist */}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Register;
