import React, { useState, useEffect, useMemo } from 'react';
import { io } from 'socket.io-client';
const socket = io('http://localhost:8000');
const AjastinKello = ({ teamId, task }) => {
  const [elapsedTime, setElapsedTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const intervalIdRef = React.useRef(null);

  useEffect(() => {
    return () => {
      clearInterval(intervalIdRef.current);
    };
  }, []);

  useEffect(() => {
    if (isRunning) {
      const intervalId = setInterval(() => {
        setElapsedTime((prevElapsedTime) => prevElapsedTime + 1);
      }, 10);

      intervalIdRef.current = intervalId;
    } else {
      clearInterval(intervalIdRef.current);
    }

    // Emit start/stop events to the WebSocket
    socket.emit('timerUpdate', {
      teamId,
      taskName: task.tehtävä_nimi,
      isRunning,
    });

    return () => {
      clearInterval(intervalIdRef.current);
    };
  }, [isRunning, teamId, task.tehtävä_nimi]);

  const startTimer = () => {
    setIsRunning(true);
  };

  const stopTimer = () => {
    setIsRunning(false);
  };

  const resetTimer = () => {
    setElapsedTime(0);
    clearInterval(intervalIdRef.current);
    setIsRunning(false);
  };

  const formatTime = (timeInMilliseconds) => {
    const minutes = Math.floor(timeInMilliseconds / (60 * 1000));
    const seconds = Math.floor((timeInMilliseconds % (60 * 1000)) / 1000);
    const milliseconds = Math.floor((timeInMilliseconds % 1000) / 10);

    return `${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}:${milliseconds < 10 ? '0' : ''}${milliseconds}`;
  };

  return (
    <div>
      <h3>{task.tehtävä_nimi}</h3>
      <div className="timer">
        <span>{formatTime(elapsedTime * 10)}</span>
      </div>
      <div className="controls">
        <button onClick={startTimer} disabled={isRunning}>
          Start
        </button>
        <button onClick={stopTimer} disabled={!isRunning}>
          Stop
        </button>
        <button onClick={resetTimer}>Reset</button>
      </div>
    </div>
  );
};

export default AjastinKello;
