import React, { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:8000');

const NewAjastinChild = () => {
  const [elapsedTime, setElapsedTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const [laps, setLaps] = useState([]); // State to store lap times
  const [lapCount, setLapCount] = useState({}); // State to track the lap count for each team
  const intervalIdRef = useRef(null);

  useEffect(() => {
    const startTimerHandler = (data) => {
      const serverTimestamp = data.timestamp;
      const localTimestamp = window.performance.now();
      const networkDelay = localTimestamp - serverTimestamp;

      const adjustedStartTime = serverTimestamp + networkDelay;

      console.log('Timer started at (adjusted):', adjustedStartTime);

      setStartTime(adjustedStartTime);
      setIsRunning(true);
    };

    const lapTimerHandler = (lapTimerData) => {
        console.log('Received lap timer event:', lapTimerData);
      
        const { teamId, taskName } = lapTimerData;
      
        if (!startTime) {
          console.error('Error: startTime is not set.');
          return;
        }
      
        const lapTime = elapsedTime * 10; // Capture the elapsed time when the lap timer is received
      
        console.log(`Team: ${teamId}, Task: ${taskName}, Lap Time: ${lapTime} milliseconds`);
      
        // Update the lap count for the team
        setLapCount((prevLapCount) => ({
          ...prevLapCount,
          [teamId]: (prevLapCount[teamId] || 0) + 1,
        }));
      
        // Update the laps state with the new lap time
        setLaps((prevLaps) => [
          ...prevLaps,
          { teamId, taskName, lapTime: lapTime - (prevLaps.length > 0 ? prevLaps[prevLaps.length - 1].lapTime : 0) },
        ]);
      };
      
      

    // Listen for timerStart event from the server
    socket.on('timerStart', startTimerHandler);

    // Listen for lapTimer event from the server
    socket.on('lapTimerUpdate', lapTimerHandler);

    return () => {
      // Clean up event listeners when the component unmounts
      socket.off('timerStart', startTimerHandler);
      socket.off('lapTimerUpdate', lapTimerHandler);
    };
  }, [startTime, elapsedTime]);

  useEffect(() => {
    if (isRunning) {
      const intervalId = setInterval(() => {
        setElapsedTime((prevElapsedTime) => prevElapsedTime + 1);
      }, 10);
      intervalIdRef.current = intervalId;
    } else {
      clearInterval(intervalIdRef.current);
    }

    return () => {
      clearInterval(intervalIdRef.current);
    };
  }, [isRunning, elapsedTime]);

  const getCombinedTime = (teamId) => {
    // Get the laps for the specific team
    const teamLaps = laps.filter((lap) => lap.teamId === teamId);
    // Calculate the combined time for the teamconsole.log(teamLaps);
    const combinedTime = teamLaps.reduce((totalTime, lap) => totalTime + lap.lapTime, 0);
    return combinedTime;
  };

  return (
    <div>
      <h3>Task Timer</h3>
      <div className="timer">
        <span>{formatTime(elapsedTime * 10)}</span>
      </div>
      <div>
        <h4>Lap Times:</h4>
        <ul>
          {laps.map((lap, index) => (
            <li key={index}>
              Team: {lap.teamId}, Task: {lap.taskName}, Lap Time: {formatTime(lap.lapTime)} milliseconds
            </li>
          ))}
        </ul>
      </div>
      <div>
        <h4>Combined Times:</h4>
        <ul>
          {Object.keys(lapCount).map((teamId) => (
            <li key={teamId}>
              Team: {teamId}, Combined Time: {formatTime(getCombinedTime(teamId))}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

  
  

const formatTime = (milliseconds) => {
  const totalSeconds = Math.floor(milliseconds / 1000);
  const minutes = Math.floor(totalSeconds / 60);
  const remainingSeconds = totalSeconds % 60;
  const hundredths = Math.floor((milliseconds % 1000) / 10);

  return `${String(minutes).padStart(2, '0')}:${String(remainingSeconds).padStart(2, '0')}:${String(hundredths).padStart(2, '0')}`;
};

export default NewAjastinChild;
