function CompetitionAlkuerä() {
  const [teams, setTeams] = useState([]);
  const [groupedTeams, setGroupedTeams] = useState([]);

  // Initialize groups with 'Aloitus' state
  const initialGroups = Array.from({ length: 6 }, (_, groupIndex) => ({
    name: `Alkuerä ${groupIndex + 1}`,
    state: 'Aloitus',
    teams: [], // Add an array to store teams for each group
  }));

  const handleDataFetched = (data) => {
    const sortedTeams = [...data].sort((a, b) => a.osallistumis_nro - b.osallistumis_nro);
    const first36Teams = sortedTeams.slice(0, 36);

    // Divide teams into groups and add the 'Aloitus' state
    const groupsWithTeams = initialGroups.map((group, index) => ({
      ...group,
      teams: first36Teams.slice(index * 6, (index + 1) * 6),
    }));

    setTeams(first36Teams);
    setGroupedTeams(groupsWithTeams);
  };

  const toggleGroupState = (groupIndex) => {
    setGroupedTeams((prevGroups) => {
      const updatedGroups = [...prevGroups];
      const group = updatedGroups[groupIndex];
      group.state = group.state === 'Aloitus' ? 'Lopetus' : 'Aloitus';
      return updatedGroups;
    });
  };

  useEffect(() => {
    // Fetch teams and initialize groups
  }, []);

  return (
    <div>
      <h1>Competition Alkuerä</h1>
      <div className="groups-container">
        {groupedTeams.map((group, groupIndex) => (
          <div key={groupIndex} className="group">
            <h2>
              {group.name} - {group.state}
            </h2>
            <button onClick={() => toggleGroupState(groupIndex)}>
              Toggle State
            </button>
            {group.teams.map((team) => (
              <div key={team.joukkue_id} className="team">
                {team.joukkue_nimi}
              </div>
            ))}
          </div>
        ))}
      </div>
      <CompetitionFetch onDataFetched={handleDataFetched} />
    </div>
  );
}

export default CompetitionAlkuerä;
