import React, { useState, useEffect } from 'react';
import '../css/JoukkueEdit.css';

function TasksEdit({ task, onUpdate, onExitEdit }) {
  const [formData, setFormData] = useState({
    tehtävä_nimi: task.tehtävä_nimi || '', // Initialize with task data or empty string
    max_aika: task.max_aika || '', // Initialize with task data or empty string
  });
  
  // Use an effect to update the form data when a new task is selected
  useEffect(() => {
    setFormData({
      tehtävä_nimi: task.tehtävä_nimi || '',
      max_aika: task.max_aika || '',
    });
  }, [task]);
  


  return (
    <div className="register-container">
      <form onSubmit={handleSubmit}>
        <div className="form-row">
        <h2>Muokkaa Joukkuetta</h2>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="joukkue_nimi"
              placeholder="Name"
              value={formData.joukkue_nimi}
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Joukkue Nimi</label>
          </div>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="kaupunki"
              placeholder="City"
              value={formData.kaupunki}
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Kaupunki</label>
          </div>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="jäsenet"
              placeholder="Members"
              value={formData.jäsenet}
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Jäsenet</label>
          </div>
        </div>
        <button type="submit" className="submit-button">
  Update Task
</button>

        <button onClick={onExitEdit}className="submit-button">Cancel</button>
      </form>
    </div>
  );
}


export default JoukkueEdit;
