import React, { useState } from 'react';
import axios from 'axios';
import Joukkueet from './Joukkueeet';
import '../css/Register.css';

function Register() {
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    jäsenet: '',
    kaupunki: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    // Send the form data to your API
    axios
      .post('http://localhost:3001/api/joukkueet', formData)
      .then((response) => {
        // Handle the response data, e.g., show a success message or reset the form.
        console.log('Joukkue created:', response.data);
        // You can reset the form or show a success message here.
      })
      .catch((error) => {
        // Handle errors, e.g., show an error message.
        console.error('Error creating joukkue:', error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

 return (
    <div className="register-container">
      <h2>Ilmoittautuminen</h2>
      <form>
        <div className="form-row">
          <div className="form-group">
            <input
              type="text"
              name="joukkue_nimi"
              value={formData.joukkue_nimi}
              onChange={handleInputChange}
              required
            />
            <label htmlFor="joukkue_nimi">Joukkue Nimi</label>
          </div>
          <div className="form-group">
            <input
              type="text"
              name="kaupunki"
              value={formData.kaupunki}
              onChange={handleInputChange}
              required
            />
            <label htmlFor="kaupunki">Kaupunki</label>
          </div>
          <div className="form-group">
            <textarea
              name="jäsenet"
              value={formData.jäsenet}
              onChange={handleInputChange}
              required
            />
            <label htmlFor="jäsenet">Jäsenet</label>
          </div>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Register;
