import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import '../css/Competition.css';

function Competition() {
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [finishedTimes, setFinishedTimes] = useState({});
  const [updatedGroupData, setUpdatedGroupData] = useState(null);

  useEffect(() => {
    const socket = io('http://localhost:8000');

    socket.on('selectedGroupUpdate', (updatedGroupData) => {
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);
      setUpdatedGroupData(parsedData);
      localStorage.setItem('selectedGroup', JSON.stringify(parsedData));
    });

    socket.on('groupDataUpdate', (updatedGroupData) => {
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);
      setUpdatedGroupData(parsedData);
      localStorage.setItem('selectedGroup', JSON.stringify(parsedData));
    });

    socket.on('finishedTimeUpdate', (finishedTimes) => {
      console.log('Received finished time update:', finishedTimes);

      setFinishedTimes(finishedTimes);

      localStorage.setItem('finishedTimes', JSON.stringify(finishedTimes));

      console.log('Updated finishedTimes:', finishedTimes);
    });

    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
      setUpdatedGroupData(parsedData);
    }

    return () => {
      if (socket.connected) {
        socket.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    const storedFinishedTimes = localStorage.getItem('finishedTimes');
    if (storedFinishedTimes) {
      const parsedFinishedTimes = JSON.parse(storedFinishedTimes);
      setFinishedTimes(parsedFinishedTimes);
      console.log('Parsed Finished Times:', parsedFinishedTimes);
    }
  }, []);

  function formatTime(milliseconds) {
    const totalSeconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(totalSeconds / 60);
    const remainingSeconds = totalSeconds % 60;
    const hundredths = Math.floor((milliseconds % 1000) / 10);

    const formattedTime = `${String(minutes).padStart(2, '0')}:${String(remainingSeconds).padStart(2, '0')}:${String(hundredths).padStart(2, '0')}`;
    return formattedTime;
  }

  const getBackgroundColor = (teamId) => {
    const teamTimeData = finishedTimes[teamId];
  
    if (!updatedGroupData || !updatedGroupData.teams || !teamTimeData) {
      return 'transparent';
    }
  
    const storedFinishedTimes = localStorage.getItem('finishedTimes');
    const storedFinishedTimesParsed = JSON.parse(storedFinishedTimes) || {};
  
    const combinedTimes = updatedGroupData.teams
      .filter((team) => storedFinishedTimesParsed.hasOwnProperty(team.joukkue_id))
      .map((team) => {
        const tasks = storedFinishedTimesParsed[team.joukkue_id][updatedGroupData.groupInfo];
        return Object.values(tasks).reduce((acc, task) => acc + task.finishedTime, 0);
      });
  
    const time = combinedTimes.find((t) => t !== undefined && t !== null);
  
    if (!time) {
      return 'transparent';
    }
  
    const validTimes = combinedTimes.filter((time) => typeof time === 'number');
  
    const sortedTimes = validTimes.sort((timeA, timeB) => timeA - timeB);
  
    const teamRank = sortedTimes.findIndex((t) => t === time) + 1;
  
    const tiedTeams = sortedTimes.filter((t) => t === time);
  
    if (tiedTeams.length > 1) {
      return teamRank <= 3 ? ['gold', 'silver', 'bronze'][teamRank - 1] : 'transparent';
    }
  
    switch (teamRank) {
      case 1:
        return 'gold';
      case 2:
        return 'silver';
      case 3:
        return 'bronze';
      default:
        return 'transparent';
    }
  };
  

  
  
  

  return (
    <div>
      <h1>Competition</h1>
      {updatedGroupData ? (
        <div>
          <h2>{updatedGroupData.groupInfo}</h2>
          <h3>Selected Tasks: {updatedGroupData.selectedTasks.map((task) => `${task.tehtävä_nimi} (${formatTime(task.max_aika * 1000)})`).join(', ')}</h3>
          <ul>
            {updatedGroupData.teams.map((team) => (
              <div key={team.joukkue_id} style={{ backgroundColor: getBackgroundColor(team.joukkue_id) }}>
                <h3>{team.joukkue_nimi}</h3>
                {Object.entries(finishedTimes[team.joukkue_id] || {}).map(([era, tasks]) => (
                  <div key={era}>
                    {Object.entries(tasks).map(([taskName, finishedTimeData]) => (
                      <div key={taskName}>
                        <h4>{taskName}</h4>
                        <p>Finished Time: {formatTime(finishedTimeData.finishedTime * 10)}</p>
                      </div>
                    ))}
                    <div>
                      <h4>Combined Time</h4>
                      <p>{formatTime(Object.values(tasks).reduce((acc, task) => acc + task.finishedTime, 0) * 10)}</p>
                    </div>
                  </div>
                ))}
                <p>Members: {team.jäsenet}</p>
              </div>
            ))}
          </ul>
        </div>
      ) : (
        <p>No selected group available.</p>
      )}
    </div>
  );
}

export default Competition;
