import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Joukkueet from './Joukkueet';
import '../css/Register.css';

function Register() {
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    jäsenet: '',
    kaupunki: '',
  });
  const [newlyAddedTeam, setNewlyAddedTeam] = useState(null);

  useEffect(() => {
    if (newlyAddedTeam) {
      // Update Joukkueet component when a new team is added
      // You can pass this team to Joukkueet as a prop or use a context API
      // Example: <Joukkueet newlyAddedTeam={newlyAddedTeam} />
    }
  }, [newlyAddedTeam]);

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post('http://localhost:3001/api/joukkueet', formData)
      .then((response) => {
        console.log('Joukkue created:', response.data);
        setNewlyAddedTeam(response.data); // Store the newly added team
      })
      .catch((error) => {
        console.error('Error creating joukkue:', error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div className="register-container">
      <h2>Ilmoittautuminen</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
        <div class="form__group field">
            <input
              type="text"
              class="form__field"
              name="joukkue_nimi"
              placeholder="Name" 
              value={formData.joukkue_nimi}
              onChange={handleInputChange}
              required
            />
            <label class="form__label">Joukkue Nimi</label>
          </div>
          <div class="form__group field">
            <input
              type="text"
              class="form__field"
              name="kaupunki"
              placeholder="Name"
              value={formData.kaupunki}
              onChange={handleInputChange}
              required
            />
            <label class="form__label">Kaupunki</label>
          </div>
          <div class="form__group field">
            <input
              type="text"
              class="form__field"
              name="jäsenet"
              placeholder="Name"
              value={formData.jäsenet}
              onChange={handleInputChange}
              required
            />
            <label class="form__label">Jäsenet</label>
          </div>
        </div>
        <button type="submit" className="submit-button">Submit</button>
      </form>
      <div className="newly-added-team">
        {newlyAddedTeam && (
          <div>
            <h3>Newly Added Team:</h3>
            <p>Joukkue Nimi: {newlyAddedTeam.joukkue_nimi}</p>
            <p>Kaupunki: {newlyAddedTeam.kaupunki}</p>
            <p>Jäsenet: {newlyAddedTeam.jäsenet}</p>
          </div>
        )}
      </div>
      <Joukkueet />
    </div>
  );
}

export default Register;
