import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/Joukkueet.css';
import JoukkueEdit from './JoukkueEdit';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

function JoukkueDisplay({ teams, setTeams }) {
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    // Fetching teams is now managed in the parent component
    // and passed as a prop, so no need to fetch them here
  }, []);

  const handleEditTeam = (team) => {
    setSelectedTeam(team);
    setIsEditing(true);
    console.log('Selected Team:', team);
  };

  const handleExitEdit = () => {
    setSelectedTeam(null);
    setIsEditing(false);
  };

  const handleUpdateTeam = (updatedTeam) => {
    axios
      .put(`http://localhost:3001/api/joukkueet/${updatedTeam.joukkue_id}`, updatedTeam)
      .then((response) => {
        console.log('Team updated:', response.data);
        setIsEditing(false);

        // After updating, you may want to refresh the team list
        setTeams((prevTeams) => {
          // Create a new array with the updated team
          const updatedTeams = prevTeams.map((team) => {
            if (team.joukkue_id === updatedTeam.joukkue_id) {
              return updatedTeam;
            }
            return team;
          });
          return updatedTeams;
        });
      })
      .catch((error) => {
        console.error('Error updating team:', error);
      });
  };

  const handleDeleteTeam = (team) => {
    const teamId = team.joukkue_id;

    axios
      .delete(`http://localhost:3001/api/joukkueet/${teamId}`)
      .then((response) => {
        console.log('Team deleted:', response.data);

        // After deleting, you may want to refresh the team list
        setTeams((prevTeams) => prevTeams.filter((t) => t.joukkue_id !== teamId));
      })
      .catch((error) => {
        console.error('Error deleting team:', error);
      });
  };

  return (
    <div>
      <div className="form-container">
        {isEditing && (
          <JoukkueEdit
            team={selectedTeam}
            onUpdate={handleUpdateTeam}
            onExitEdit={handleExitEdit}
          />
        )}
      </div>
      <h2 className="team-heading">Joukkueet</h2>
      <ul className="team-list">
        {teams.map((team) => (
          <li key={team.joukkue_id} className="team-card">
            {team.joukkue_nimi}
            <div className="team-info">
              <EditIcon onClick={() => handleEditTeam(team)} />
              <DeleteIcon onClick={() => handleDeleteTeam(team)} />
              <p>Kaupunki: {team.kaupunki}</p>
              <p>J채senet: {team.j채senet}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default JoukkueDisplay;
