import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import '../css/Competition.css';
function Competition() {
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [finishedTimes, setFinishedTimes] = useState({});
  const [updatedGroupData, setUpdatedGroupData] = useState(null);

  useEffect(() => {
    const socket = io('http://localhost:8000');

    socket.on('selectedGroupUpdate', (updatedGroupData) => {
      console.log('Received updated group data:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);
      setUpdatedGroupData(parsedData);
      localStorage.setItem('selectedGroup', JSON.stringify(parsedData));

    });
    
    socket.on('groupDataUpdate', (updatedGroupData) => {
      console.log('Received group data update:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);
      setUpdatedGroupData(parsedData);
      localStorage.setItem('selectedGroup', JSON.stringify(parsedData));
    });


    
    

    socket.on('finishedTimeUpdate', (finishedTimeData) => {
      console.log('Received finished time data:', finishedTimeData);
    
      try {
        const parsedData = JSON.parse(finishedTimeData);
    
        if (parsedData && parsedData.teamId && parsedData.finishedTime) {
          setFinishedTimes((prevFinishedTimes) => {
            const updatedFinishedTimes = {
              ...prevFinishedTimes,
              [parsedData.teamId]: parsedData.finishedTime,
            };
    
            localStorage.setItem('finishedTimes', JSON.stringify(updatedFinishedTimes));
    
            console.log('Updated finishedTimes:', updatedFinishedTimes);
            return updatedFinishedTimes;
          });
        } else {
          console.error('Invalid finished time data:', finishedTimeData);
        }
      } catch (error) {
        console.error('Error parsing finished time data:', error);
      }
    });
    
    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
      setUpdatedGroupData(parsedData);
    }

    return () => {
      if (socket.connected) {
        socket.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    const storedFinishedTimes = localStorage.getItem('finishedTimes');
    if (storedFinishedTimes) {
      const parsedFinishedTimes = JSON.parse(storedFinishedTimes);
      setFinishedTimes(parsedFinishedTimes);
    }
  }, []);

const getBackgroundColor = (teamId) => {
  const time = finishedTimes[teamId];

  if (!updatedGroupData || !updatedGroupData.teams) {
    return 'transparent';
  }

  const teamsInSelectedGroup = updatedGroupData.teams;
  
  // Filter the finished times for teams in the selected group
  const sortedTeams = teamsInSelectedGroup
    .filter((team) => finishedTimes.hasOwnProperty(team.joukkue_id))
    .sort((a, b) => finishedTimes[a.joukkue_id] - finishedTimes[b.joukkue_id]);

  const teamRank = sortedTeams.findIndex((team) => team.joukkue_id === teamId) + 1;

  const tiedTeams = sortedTeams.filter((team) => finishedTimes[team.joukkue_id] === time)
    .map((team) => team.joukkue_id);

  if (tiedTeams.length > 1) {
    return teamRank <= 3 ? ['gold', 'silver', 'bronze'][teamRank - 1] : 'transparent';
  }

  switch (teamRank) {
    case 1:
      return 'gold';
    case 2:
      return 'silver';
    case 3:
      return 'bronze';
    default:
      return 'transparent';
  }
};

  
  
  
  
  
  
  

  return (
    <div>
      <h1>Competition</h1>
      {updatedGroupData ? (
        <div>
          <h2>{updatedGroupData.groupInfo}</h2>
          <h3>Selected Task: {updatedGroupData.selectedTask ? updatedGroupData.selectedTask.tehtävä_nimi : 'None'}</h3>
          <p>Max Aika: {updatedGroupData.selectedTask ? updatedGroupData.selectedTask.max_aika : 'N/A'}</p>
          <ul>
  {updatedGroupData.teams.map((team) => (
    <li key={team.joukkue_id} className={getBackgroundColor(team.joukkue_id)}>
      <h3>{team.joukkue_nimi}</h3>
      {finishedTimes[team.joukkue_id] && (
        <p>Finished Time: {finishedTimes[team.joukkue_id]}</p>
      )}
      <p>Members: {team.jäsenet}</p>
    </li>
  ))}
</ul>

        </div>
      ) : (
        <p>No selected group available.</p>
      )}
    </div>
  );
}

export default Competition;
