import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';

function Ajastin() {
  const { teamId } = useParams();
  const [selectedGroup, setSelectedGroup] = useState(null);

  useEffect(() => {
    document.title = `Team ${teamId}`;
    const socket = io('http://localhost:8000');

    socket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    socket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    socket.on('selectedGroupUpdate', (updatedGroupData) => {
      console.log('Received updated group data in Ajastin:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);
    });

    return () => {
      if (socket.connected) {
        socket.disconnect();
      }
    };
  }, [teamId]);

  return (
    <div>
      <h2>{`Team ${teamId} Stopwatch`}</h2>
      {selectedGroup && (
        <div>
          <h3>{`Selected Task: ${selectedGroup.selectedTask.tehtävä_nimi}`}</h3>
          <h3>{`Max Time: ${selectedGroup.selectedTask.max_aika}`}</h3>
        </div>
      )}
      {/* Rest of the component */}
    </div>
  );
}

export default Ajastin;
