import React, { useState, useEffect } from 'react';

function JoukkueEdit({ joukkue, onUpdate }) {
  // Use an effect to initialize the state only when joukkue data is available
  useEffect(() => {
    if (joukkue) {
      setFormData({ ...joukkue });
    }
  }, [joukkue]);

  const [formData, setFormData] = useState({});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdate(formData);
  };

  if (!joukkue) {
    return null; // Handle the case where joukkue is not available
  }

  return (
    <div>
      <h2>Edit Joukkue</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="joukkue_nimi"
              placeholder="Name"
              value={formData.joukkue_nimi || ''} // Provide a default value to prevent undefined
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Joukkue Nimi</label>
          </div>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="kaupunki"
              placeholder="City"
              value={formData.kaupunki || ''} // Provide a default value to prevent undefined
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Kaupunki</label>
          </div>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="jäsenet"
              placeholder="Members"
              value={formData.jäsenet || ''} // Provide a default value to prevent undefined
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Jäsenet</label>
          </div>
        </div>
        <button type="submit" className="submit-button">
          Update
        </button>
      </form>
    </div>
  );
}

export default JoukkueEdit;
