import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';

function TasksSelector({ onSelectTask }) {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState({});


  useEffect(() => {
    axios.get('http://localhost:3001/api/tehtavat')
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });
  }, []);

  const handleTaskSelect = (task) => {
    if (task && task.tehtävä_id) {
      const selectedTaskData = {
        tehtävä_id: task.tehtävä_id,
        // Add other relevant task properties
      };
  
      setSelectedTask(selectedTaskData);
  
      const socket = io('http://localhost:8000');
      socket.emit('selectedTaskUpdate', selectedTaskData);
  
      if (onSelectTask) {
        onSelectTask(selectedTaskData);
      }
    } else {
      console.error('Invalid task object:', task);
    }
  };
  
  

  return (
    <div>
      <h2>Task Selector</h2>
      <select
  value={selectedTask ? selectedTask.id : ''}
  onChange={(e) => {
    const taskId = e.target.value;
    const selected = tasks.find((task) => task.tehtävä_id === parseInt(taskId, 10));
  
    if (selected) {
      handleTaskSelect(selected);
    } else {
      console.error('Invalid task selected:', taskId);
    }
  }}
  
  
>
  <option key="default" value="0">Select a task</option>
  {tasks.map((task) => {

    return (
        <option key={task.tehtävä_nimi} value={task.id}>
        {task.tehtävä_nimi}
      </option>
    );
  })}
</select>




    </div>
  );
}

export default TasksSelector;
