import React, { useState } from 'react';
import axios from 'axios';

function Register() {
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    jäsenet: '',
    kaupunki: '',
    osallistumis_nro: '',
    semifinalist: false,
    finalist: false,
  });

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  }
  
  const handleSubmit = (e) => {
    e.preventDefault();

    // Send a POST request to your API endpoint
    axios.post('http://localhost:3001/api/joukkueet', formData)
    .then((response) => {
      console.log(response.data); // Handle the response as needed
    })
    .catch((error) => {
      console.error(error); // Handle any errors
    });
}

  return (
    <div>
      <h2>Register a New Joukkue</h2>
      <form onSubmit={handleSubmit}>
        <label>Joukkue Nimi:
          <input type="text" name="joukkue_nimi" onChange={handleInputChange} value={formData.joukkue_nimi} />
        </label>
        <br />
        <label>Jäsenet:
          <input type="text" name="jäsenet" onChange={handleInputChange} value={formData.jäsenet} />
        </label>
        <br />
        <label>Kaupunki:
          <input type="text" name="kaupunki" onChange={handleInputChange} value={formData.kaupunki} />
        </label>
        <br />
        <label>Osallistumis Numero:
          <input type="text" name="osallistumis_nro" onChange={handleInputChange} value={formData.osallistumis_nro} />
        </label>
        <br />
        <label>Semifinalist:
          <input type="checkbox" name="semifinalist" onChange={handleInputChange} checked={formData.semifinalist} />
        </label>
        <br />
        <label>Finalist:
          <input type="checkbox" name="finalist" onChange={handleInputChange} checked={formData.finalist} />
        </label>
        <br />
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default Register;
