import React, { useState, useEffect } from 'react';

const AlkuEräLopetus = ({ eraName }) => {
  const [times, setTimes] = useState([]);

  useEffect(() => {
    fetchTimes();
  }, []);

  const fetchTimes = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/aikataulu');
      if (!response.ok) {
        throw new Error('Failed to fetch times');
      }

      const data = await response.json();
      setTimes(data);
    } catch (error) {
      console.error('Error fetching times:', error);
    }
  };

  const groupTimesByEra = (times, eraName) => {
    const filteredTimes = times.filter((time) => time.era.includes(eraName));
  
    const groupedTimes = {};
    filteredTimes.forEach((time) => {
      const eraNumber = time.era.match(/\d+/);
      if (eraNumber) {
        const key = `Era ${eraNumber[0]}`;
        if (!groupedTimes[key]) {
          groupedTimes[key] = [];
        }
        groupedTimes[key].push(time);
      }
    });
  
    return groupedTimes;
  };

  const handleLopetaClick = async () => {
    try {
      await fetchTimes();
  
      if (times && times.length > 0) {
        const groupedByEra = groupTimesByEra(times, 'Alkuerä');
        console.log('Grouped by era:', groupedByEra);
  
        const winnersByRound = {};
        const allWinners = [];
  
        // Loop through each round
        Object.keys(groupedByEra).forEach((eraKey, index) => {
          const roundKey = `Alkuerä ${index + 1}`;
  
          // Sort teams in the current round by combined time
          const sortedTeams = groupedByEra[eraKey]
            .sort((a, b) => a.aika - b.aika);
  
          // Get the top 6 winners from the current round
          const roundWinners = sortedTeams.slice(0, 6);
  
          // Add round winners to the winnersByRound object
          winnersByRound[roundKey] = roundWinners;
  
          // Add round winners to the allWinners array
          allWinners.push(...roundWinners);
        });
  
        // Ensure allWinners only contains unique teams
        const uniqueWinners = Array.from(new Set(allWinners.map((winner) => winner.joukkue_id)))
          .map((teamId) => ({
            teamId,
            combinedTime: allWinners.find((winner) => winner.joukkue_id === teamId).aika,
          }));
  
        // Get additional winners from the remaining teams (up to 6) based on combined time
        const additionalWinners = uniqueWinners
          .sort((a, b) => a.combinedTime - b.combinedTime)
          .slice(0, 6);
  
        // Add additional winners to the winnersByRound object
        winnersByRound['Additional Winners'] = additionalWinners;
  
        console.log('Winners by Round:', winnersByRound);
      } else {
        alert('No times available.');
      }
    } catch (error) {
      console.error('Error handling lopeta click:', error);
    }
  };
  

  return (
    <div>
      <button onClick={handleLopetaClick}>Lopeta erä</button>
    </div>
  );
};

export default AlkuEräLopetus;
