import React, { useState, useEffect } from 'react';

const AlkuEräLopetus = ({ eraName }) => {
  const [times, setTimes] = useState([]);

  useEffect(() => {
    fetchTimes();
  }, []);

  const fetchTimes = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/aikataulu');
      if (!response.ok) {
        throw new Error('Failed to fetch times');
      }

      const data = await response.json();
      setTimes(data);
    } catch (error) {
      console.error('Error fetching times:', error);
    }
  };

  const calculateCombinedTimesByRound = () => {
    const combinedTimesByRound = {};

    times.forEach((time) => {
      const teamId = time.joukkue_id;
      const round = time.era;

      // Calculate combined time for each team in each round
      if (!combinedTimesByRound[round]) {
        combinedTimesByRound[round] = {};
      }

      if (!combinedTimesByRound[round][teamId]) {
        combinedTimesByRound[round][teamId] = 0;
      }

      combinedTimesByRound[round][teamId] += time.aika;
    });

    return combinedTimesByRound;
  };

  const findWinnersByRound = () => {
    const combinedTimesByRound = calculateCombinedTimesByRound();
    const winnersByRound = {};
    const allWinners = []; // Keep track of all winners
    const allNonWinners = []; // Keep track of all non-winners
  
    Object.keys(combinedTimesByRound).forEach((round) => {
      const combinedTimesByTeam = combinedTimesByRound[round];
      const roundWinners = findRoundWinners(combinedTimesByTeam);
  
      // Add round winners to the list of all winners
      allWinners.push(...roundWinners);
  
      // Store round winners
      winnersByRound[round] = roundWinners;
  
      // Find non-winners for the current round
      const nonWinners = Object.keys(combinedTimesByTeam)
        .filter((teamId) => !roundWinners.some((winner) => winner.teamId === teamId))
        .map((teamId) => ({ teamId, combinedTime: combinedTimesByTeam[teamId] }));
  
      // Add non-winners to the list of all non-winners
      allNonWinners.push(...nonWinners);
  
      // Log non-winners for the current round
      console.log(`Non-winners for ${round}:`, nonWinners);
    });
  
    // Now you have a list of all winners in 'allWinners'
    // And a list of all non-winners in 'allNonWinners'
  
    // Log all non-winners
    console.log('All non-winners:', allNonWinners);
  
    return winnersByRound;
  };
  
  
  

  const findRoundWinners = (combinedTimesByTeam) => {
    const roundWinners = [];
    Object.keys(combinedTimesByTeam).forEach((teamId) => {
      const combinedTime = combinedTimesByTeam[teamId];

      // Compare combined time with other teams in the round
      const isWinner = Object.values(combinedTimesByTeam).every(
        (otherCombinedTime) => combinedTime <= otherCombinedTime
      );

      if (isWinner) {
        roundWinners.push({ teamId, combinedTime });
      }
    });

    return roundWinners;
  };

  const handleLopetaClick = async () => {
  try {
    // Fetch and process times
    const winnersByRound = findWinnersByRound();

    // Get all non-winners
    const allNonWinners = winnersByRound.reduce(
      (acc, roundWinners) => [...acc, ...roundWinners.nonWinners],
      []
    );

    // Sort non-winners by combined time in ascending order
    const sortedNonWinners = allNonWinners.sort((a, b) => a.combinedTime - b.combinedTime);

    // Get the 6 best times from non-winners
    const sixBestNonWinners = sortedNonWinners.slice(0, 6);

    // Combine the winners and the 6 best non-winners to get the final list of 12 winners
    const allWinners = winnersByRound.reduce(
      (acc, roundWinners) => [...acc, ...roundWinners.winners],
      []
    );

    const finalWinnersList = [...allWinners, ...sixBestNonWinners];

    // Update 'semifinalist' for winners and 'kerailyera' for non-winners
    const updatePromises = finalWinnersList.map(async (winner) => {
      const { teamId, combinedTime } = winner;
      const updatedFields = combinedTime ? { semifinalist: 1 } : { kerailyera: 1 };

      try {
        // Use your API endpoint to update the team
        const response = await fetch('http://localhost:3001/api/updateJoukkueet', {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify([{ joukkueId: teamId, updatedFields }]),
        });

        if (!response.ok) {
          throw new Error(`Failed to update team ${teamId}`);
        }

        return { teamId, success: true };
      } catch (error) {
        console.error(`Error updating team ${teamId}:`, error);
        return { teamId, success: false, error: error.message };
      }
    });

    const updateResults = await Promise.all(updatePromises);

    // Display or use update results as needed
    console.log('Update results:', updateResults);
  } catch (error) {
    console.error('Error handling lopeta click:', error);
  }
};

  

  return (
    <div>
      <button onClick={handleLopetaClick}>Lopeta erä</button>
    </div>
  );
};

export default AlkuEräLopetus;
