import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';

function Competition() {
  const [selectedGroups, setSelectedGroups] = useState([]);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    // Initialize the WebSocket connection when the component mounts
    const newSocket = io('http://localhost:8000');

    newSocket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    newSocket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    newSocket.on('updateSelectedGroup', (selectedGroupData) => {
      // Parse the JSON string to obtain an array of objects
      
    
      setSelectedGroups((prevSelectedGroups) => [
        ...prevSelectedGroups,
        JSON.parse(selectedGroupData),
      ]);
    });

    setSocket(newSocket);

    return () => {
      // Close the WebSocket connection when the component unmounts
      if (newSocket && newSocket.connected) {
        newSocket.disconnect();
      }
    };
  }, []);
  

  return (
    <div>
      <h1>Competition</h1>
      <ul>
        {selectedGroups.map((group) => (
          <li key={group.joukkue_id}>
            <h2>Group {group.joukkue_id}</h2>
            <p>Team Name: {group.joukkue_nimi}</p>
            <p>Members: {group.jäsenet}</p>
            <p>City: {group.kaupunki}</p>
            <p>Participation Number: {group.osallistumis_nro}</p>
            <p>Semifinalist: {group.semifinalist}</p>
            <p>Finalist: {group.finalist}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Competition;
