import React from 'react';

function Competition({ selectedGroup }) {
  if (selectedGroup === null) {
    return <p>Please select a group.</p>;
  }

  if (selectedGroup && selectedGroup.teams && selectedGroup.teams.length > 0) {
    // Access the data for the selected group and render it as needed
    const groupData = selectedGroup; // You can customize this based on your data structure

    return (
      <div>
        <h2>Selected Group</h2>
        <p>Group Name: {groupData.groupName}</p>
        <p>Group Teams:</p>
        <ul>
          {groupData.teams.map((team) => (
            <li key={team.joukkue_id}>{team.joukkue_nimi}</li>
          ))}
        </ul>
      </div>
    );
  } else {
    return <p>No data available for the selected group.</p>;
  }
}

export default Competition;
