import React, { useState } from 'react';
import axios from 'axios';
import Joukkueet from './Joukkueet';
import JoukkueEdit from './JoukkueEdit';
import '../css/Register.css';

function Register() {
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    jäsenet: '',
    kaupunki: '',
  });
  const [newlyAddedTeam, setNewlyAddedTeam] = useState(null);
  const [key, setKey] = useState(0);
  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post('http://localhost:3001/api/joukkueet', formData)
      .then((response) => {
        console.log('Joukkue created:', response.data);
        setNewlyAddedTeam(response.data);
        setKey(key + 1); // Change the key to remount Joukkueet
        setFormData({
          joukkue_nimi: '',
          jäsenet: '',
          kaupunki: '',
        });
      })
      .catch((error) => {
        console.error('Error creating joukkue:', error);
      });
  };
  
  const handleEdit = () => {
    setIsEditing(true); // Enter edit mode
  };

  const handleCancelEdit = () => {
    setIsEditing(false); // Cancel edit mode
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div className="register-container">
      <h2>Ilmoittautuminen</h2>
      <div className="form-container">
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              id="joukkue_nimi"
              name="joukkue_nimi"
              placeholder="Name"
              value={formData.joukkue_nimi}
              onChange={handleInputChange}
              required
              autoComplete="off"
            />
            <label htmlFor="joukkue_nimi" className="form__label">Joukkue Nimi</label>
          </div>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              id="kaupunki"
              name="kaupunki"
              placeholder="Name"
              value={formData.kaupunki}
              onChange={handleInputChange}
              required
              autoComplete="off"
            />
            <label htmlFor="kaupunki" className="form__label">Kaupunki</label>
          </div>
          <div className="form__group">
          <input
              type="text"
              className="form__field"
              id="jäsenet"
              name="jäsenet"
              placeholder="Name"
              value={formData.jäsenet}
              onChange={handleInputChange}
              required
              autoComplete="off"
            />
            <label htmlFor="jäsenet" className="form__label">Jäsenet</label>
          </div>
        </div>
        <button type="submit" className="submit-button">Submit</button>
        </form>
    {newlyAddedTeam && (
      <div className="newly-added-team">
        <h3>Uusi joukkue lisätty:</h3>
        <p>Joukkueen Nimi: {newlyAddedTeam.joukkue_nimi}</p>
        <p>Kaupunki: {newlyAddedTeam.kaupunki}</p>
        <p>Jäsenet: {newlyAddedTeam.jäsenet}</p>
      </div>
    )}
              <button type="button" onClick={handleEdit}>
            Edit
          </button>
  </div>
  <Joukkueet key={key} />
</div>
  );
}

export default Register;
