import React, { useState, useEffect } from 'react';

const EräLopetus = ({ eraName }) => {
  const [times, setTimes] = useState([]);

  useEffect(() => {
    // Fetch times when the component mounts
    fetchTimes();
  }, []);

  const fetchTimes = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/aikataulu');
      if (!response.ok) {
        throw new Error('Failed to fetch times');
      }

      const data = await response.json();
      setTimes(data.result); // Assuming your API response structure has a "result" property with the times
    } catch (error) {
      console.error('Error fetching times:', error);
    }
  };

  const handleLopetaEräClick = () => {
    // Display the era name and the fetched times in the alert
    alert(`Lopetit erän '${eraName}'\nTimes:\n${times.map(time => time.aika).join('\n')}`);
    // You can perform additional actions here if needed
  };

  return (
    <div>
      <button onClick={handleLopetaEräClick}>Lopeta erä</button>
    </div>
  );
};

export default EräLopetus;
