import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TasksEdit from './TasksEdit'; // Create the TehtavaEdit component for task editing
import TasksRegister from './TasksRegister'; // Create the TasksRegister component for task registration
import TasksDisplay from './TasksDisplay'; // Create the TasksDisplay component for displaying individual tasks

function Tasks() {
  const [tehtävät, setTehtävät] = useState([]); // Change "teams" to "tehtävät"
  const [selectedTask, setSelectedTask] = useState(null); // Change "selectedTeam" to "selectedTask"
  const [isEditing, setIsEditing] = useState(false);

  function fetchTasks() {
    axios
      .get('http://localhost:3001/api/tehtavat') // Change the URL to the tasks endpoint
      .then((response) => {
        setTehtävät(response.data); // Change "teams" to "tehtävät"
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });
  }

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleEditTask = (task) => {
    setSelectedTask(task); // Change "selectedTeam" to "selectedTask"
    setIsEditing(true);
    console.log('Selected Task:', task);
  };

  const handleExitEdit = () => {
    resetEditing();
  };

  const resetEditing = () => {
    setSelectedTask(null); // Change "selectedTeam" to "selectedTask"
    setIsEditing(false);
  };

  const handleUpdateTask = (updatedTask) => {
    axios
      .put(`http://localhost:3001/api/tehtavat/${updatedTask.id}`, updatedTask) // Change "joukkue_id" to "id"
      .then((response) => {
        console.log('Task updated:', response.data);
        setIsEditing(false);
        fetchTasks();
      })
      .catch((error) => {
        console.error('Error updating task:', error);
      });
  };

  const handleDeleteTask = (task) => {
    const taskId = task.id; // Change "joukkue_id" to "id"

    if (taskId) {
      axios
        .delete(`http://localhost:3001/api/tehtavat/${taskId}`) // Change the URL to the tasks endpoint
        .then((response) => {
          console.log('Task deleted:', response.data);
          fetchTasks();
        })
        .catch((error) => {
          console.error('Error deleting task:', error);
        });
    } else {
      console.log('Invalid taskId, cannot delete the task.');
    }
  };

  return (
    <div>
      <div className="form-container">
        <TasksRegister setTehtävät={setTehtävät} /> {/* Change "setTeams" to "setTehtävät" */}
        {isEditing && (
          <TasksEdit
            task={selectedTask} // Change "team" to "task"
            onUpdate={handleUpdateTask}
            onExitEdit={handleExitEdit}
          />
        )}
      </div>
      <TasksDisplay
        tehtävät={tehtävät} // Change "teams" to "tehtävät"
        onEditTask={handleEditTask}
        onDeleteTask={handleDeleteTask}
      />
    </div>
  );
}

export default Tehtavat; // Change the export name to "Tehtavat"
