import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

function Competition() {
  const [selectedGroup, setSelectedGroup] = useState(null);

  useEffect(() => {
    const socket = io('http://localhost:8000', {
      transports: ['websocket'],
    });

    socket.on('selectedGroupUpdate', (updatedGroupData) => {
      console.log('Received updated group data:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData); // Parse the updated data
      setSelectedGroup(parsedData);

      // Store the selected group data in local storage
      localStorage.setItem('selectedGroup', updatedGroupData);
    });

    // Check if there's a selected group in local storage
    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
    }

    return () => {
      socket.disconnect();
    };
  }, []);

  return (
    <div>
      <h1>Competition</h1>
      {selectedGroup ? (
        <div>
          <h2>Group {selectedGroup[0].joukkue_id}</h2>
          <p>Team Name: {selectedGroup[0].joukkue_nimi}</p>
          <p>Members: {selectedGroup[0].jäsenet}</p>
          <p>City: {selectedGroup[0].kaupunki}</p>
          <p>Participation Number: {selectedGroup[0].osallistumis_nro}</p>
          <p>Semifinalist: {selectedGroup[0].semifinalist}</p>
          <p>Finalist: {selectedGroup[0].finalist}</p>
        </div>
      ) : (
        <p>No selected group available.</p>
      )}
    </div>
  );
}

export default Competition;
