import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TasksSelector({ onSelectTask }) {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState('');

  useEffect(() => {
    // Fetch tasks from the API
    axios.get('http://localhost:3001/api/tehtavat')
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });
  }, []);

  const handleTaskChange = (event) => {
    const taskName = event.target.value;
    setSelectedTask(taskName);
    onSelectTask(taskName);
  };

  return (
    <div>
      <label htmlFor="taskSelector">Select a Task:</label>
      <select id="taskSelector" value={selectedTask} onChange={handleTaskChange}>
        <option value="">Select a task</option>
        {tasks.map((task) => (
          <option key={task.tehtävä_id} value={task.tehtävä_nimi}>
            {task.tehtävä_nimi}
          </option>
        ))}
      </select>
    </div>
  );
}

export default TasksSelector;
