import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/Joukkueet.css';
import JoukkueEdit from './JoukkueEdit';

function Joukkueet() {
  const [teams, setTeams] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  function fetchTeams(setTeams) {
    axios
      .get('http://localhost:3001/api/joukkueet')
      .then((response) => {
        setTeams(response.data);
      })
      .catch((error) => {
        console.error('Error fetching teams:', error);
      });
  }

  useEffect(() => {
    fetchTeams(setTeams); // Fetch teams initially
  }, []);

  const handleEditTeam = (team) => {
    setSelectedTeam(team);
    setIsEditing(true);
    console.log('Selected Team:', team);
  };
  

  const handleExitEdit = () => {
    setIsEditing(false);
  };

  const handleUpdateTeam = (updatedTeam) => {
    axios
      .put(`http://localhost:3001/api/joukkueet/${updatedTeam.joukkue_id}`, updatedTeam)
      .then((response) => {
        console.log('Team updated:', response.data);
        setIsEditing(false);
        fetchTeams(setTeams); // Fetch updated team list after editing
      })
      .catch((error) => {
        console.error('Error updating team:', error);
      });
  };

  return (
    <div>
      <h2 className="team-heading">Joukkueet</h2>
      <ul className="team-list">
        {teams.map((team) => (
          <li key={team.joukkue_id} className="team-card">
            {team.joukkue_nimi}
            <div className="team-info">
            <button onClick={() => handleEditTeam(team)}>Muokkaa</button>
              <p>Kaupunki: {team.kaupunki}</p>
              <p>J채senet: {team.j채senet}</p>
            </div>
          </li>
        ))}
      </ul>
      {isEditing && (
        <JoukkueEdit
          team={selectedTeam}
          onUpdate={handleUpdateTeam} // Pass the onUpdate function
          onExitEdit={handleExitEdit}
        />
      )}
    </div>
  );
}

export default Joukkueet;
