import React, { useState } from 'react';
import axios from 'axios';

function Register({ onAddSuccess }) {
  const [joukkueNimi, setJoukkueNimi] = useState('');
  const [kaupunki, setKaupunki] = useState('');
  const [jasenet, setJasenet] = useState(''); // Corrected field name
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic form validation
    if (!joukkueNimi || !kaupunki || !jasenet) {
      setErrorMessage('All fields are required.');
      return;
    }

    // Make a POST request to the server to add the new record using Axios
    axios.post('/api/joukkueet', {
      joukkue_nimi: joukkueNimi, // Corrected field name
      kaupunki,
      jäsenet: jasenet, // Corrected field name
    })
      .then((response) => {
        const data = response.data;
        console.log('Data inserted:', data);

        // Optionally, you can perform any necessary actions after successful insertion
        setJoukkueNimi('');
        setKaupunki('');
        setJasenet('');
        setErrorMessage('');
        onAddSuccess(data); // Notify the parent component of the successful addition
      })
      .catch((error) => {
        console.error('Error adding data:', error);
        setErrorMessage('An error occurred while adding data.');
      });
  };

  return (
    <div className={styles.formContainer}>
      <h2>Lisää Joukkue</h2>
      {errorMessage && <div className={styles.formError}>{errorMessage}</div>}
      <form onSubmit={handleSubmit}>
        <label className={styles.formLabel}>
          Joukkue nimi:
          <input
            type="text"
            value={joukkueNimi}
            onChange={(e) => setJoukkueNimi(e.target.value)}
            className={styles.formInput}
          />
        </label>
        <label className={styles.formLabel}>
          Kaupunki:
          <input
            type="text"
            value={kaupunki}
            onChange={(e) => setKaupunki(e.target.value)}
            className={styles.formInput}
          />
        </label>
        <label className={styles.formLabel}>
          Jäsenet:
          <input
            type="text"
            value={jasenet}
            onChange={(e) => setJasenet(e.target.value)}
            className={styles.formInput}
          />
        </label>
        <button type="submit" className={styles.formButton}>
          Lisää joukkue
        </button>
      </form>
    </div>
  );
}

export default Register;
