import React, { useState } from 'react';
import axios from 'axios';
import Joukkueet from './Joukkueet';
import '../css/Register.css';

function Register() {
  const [formData, setFormData] = useState({
    joukkue_nimi: '',
    jäsenet: '',
    kaupunki: '',
  });
const [teams, setTeams] = useState([]); // Add state for teams
  const [newlyAddedTeam, setNewlyAddedTeam] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post('http://localhost:3001/api/joukkueet', formData)
      .then((response) => {
        console.log('Joukkue created:', response.data);
        setNewlyAddedTeam(response.data);
        updateTeamsList(); // Call the updateTeamsList callback after a new team is added
      })
      .catch((error) => {
        console.error('Error creating joukkue:', error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const updateTeamsList = () => {
    axios
      .get('http://localhost:3001/api/joukkueet')
      .then((response) => {
        // Update the teams list in the Joukkueet component
        setTeams(response.data);
      })
      .catch((error) => {
        console.error('Error fetching teams:', error);
      });
  };

  return (
    <div className="register-container">
      <h2>Ilmoittautuminen</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="joukkue_nimi"
              placeholder="Name"
              value={formData.joukkue_nimi}
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Joukkue Nimi</label>
          </div>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="kaupunki"
              placeholder="Name"
              value={formData.kaupunki}
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Kaupunki</label>
          </div>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="jäsenet"
              placeholder="Name"
              value={formData.jäsenet}
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Jäsenet</label>
          </div>
        </div>
        <button type="submit" className="submit-button">
          Submit
        </button>
      </form>
      {newlyAddedTeam && (
        <div className="newly-added-team">
          <h3>Newly Added Team:</h3>
          <p>Joukkue Nimi: {newlyAddedTeam.joukkue_nimi}</p>
          <p>Kaupunki: {newlyAddedTeam.kaupunki}</p>
          <p>Jäsenet: {newlyAddedTeam.jäsenet}</p>
        </div>
      )}
      <Joukkueet updateTeamsList={updateTeamsList} />
    </div>
  );
}

export default Register;
