import React, { useState, useEffect } from 'react';

const EräLopetus = ({ eraName }) => {
  const [times, setTimes] = useState([]);

  useEffect(() => {
    fetchTimes();
  }, []);

  const fetchTimes = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/aikataulu');
      if (!response.ok) {
        throw new Error('Failed to fetch times');
      }
  
      const data = await response.json();
      const filteredTimes = data.filter(time => time.era.includes(eraName));
      setTimes(filteredTimes);
    } catch (error) {
      console.error('Error fetching times:', error);
    }
  };

  const extractEraNumber = (era) => {
    const match = era.match(/\d+/);
    return match ? parseInt(match[0], 10) : null;
  };
  
  const determineWinners = (group) => {
    // Sort the group based on the aika values
    const sortedGroup = group.sort((a, b) => a.aika - b.aika);
  
    // Determine winners
    const winners = [];
    let currentRank = 1;
    for (let i = 0; i < sortedGroup.length; i++) {
      const team = sortedGroup[i];
      const nextTeam = sortedGroup[i + 1];
  
      winners.push({
        ...team,
        rank: currentRank,
      });
  
      if (nextTeam && nextTeam.aika !== team.aika) {
        currentRank += 1;
      }
    }
  
    return winners;
  };
  
const groupTimesByEraNumber = (times) => {
  const groupedTimes = {};
  times.forEach((time) => {
    const eraNumber = extractEraNumber(time.era);
    if (eraNumber !== null) {
      if (!groupedTimes[eraNumber]) {
        groupedTimes[eraNumber] = [];
      }
      groupedTimes[eraNumber].push(time);
    }
  });
  return groupedTimes;
};

const determineFirstPlaceWinners = (times) => {
  const groupedTimes = groupTimesByEraNumber(times);

  const firstPlaceWinners = {};
  Object.keys(groupedTimes).forEach((eraNumber) => {
    const eraGroup = groupedTimes[eraNumber];
    const winners = determineWinners(eraGroup);

    const firstPlaceWinner = winners.find((winner) => winner.rank === 1);

    if (firstPlaceWinner) {
      firstPlaceWinners[eraNumber] = [firstPlaceWinner];
    }
  });

  return firstPlaceWinners;
};

const determineAdditionalWinners = (groupedTimes, firstPlaceWinners) => {
  const allParticipants = Object.values(groupedTimes).flatMap((group) =>
    group.map((time) => time.joukkue_id)
  );

  const nonWinners = allParticipants.filter(
    (participant) =>
      !Object.values(firstPlaceWinners).some((winners) =>
        winners.some((winner) => winner.joukkue_id === participant)
      )
  );

  const additionalWinners = determineWinners(groupedTimes, nonWinners, 6);

  return additionalWinners;
};

const firstPlaceWinners = determineFirstPlaceWinners(times);

const additionalWinners = determineAdditionalWinners(groupedTimes, firstPlaceWinners);
console.log(additionalWinners);

  
  
  
  
  
  
  
  
  

  const handleLopetaEräClick = async () => {
    await fetchTimes();

    if (times && times.length > 0) {
      alert(`Lopetit erän '${eraName}`);
    } else {
      alert('No times available.');
    }
  };

  return (
    <div>
      <button onClick={handleLopetaEräClick}>Lopeta erä</button>
    </div>
  );
};

export default EräLopetus;
