import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';

function Ajastin() {
  const { teamId } = useParams();
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [tasksData, setTasksData] = useState([]);
  const [sockets, setSockets] = useState([]);
  const [elapsedTimes, setElapsedTimes] = useState([]);
  const [isRunning, setIsRunning] = useState([]);
  const [selectedTask, setSelectedTask] = useState(0); // Default to the first task

  useEffect(() => {
    document.title = `Team ${teamId}`;

    // Initialize sockets and state for each task
    const newSockets = tasksData.map(() => io('http://localhost:8000'));
    setSockets(newSockets);

    newSockets.forEach((socket, index) => {
      socket.on('connect', () => {
        console.log(`WebSocket connection is open for task ${index + 1}.`);
      });

      socket.on('disconnect', () => {
        console.log(`WebSocket connection is closed for task ${index + 1}.`);
      });

      socket.on('selectedGroupUpdate', (updatedGroupData) => {
        console.log(`Received updated group data for task ${index + 1}:`, updatedGroupData);
        const parsedData = JSON.parse(updatedGroupData);
        setSelectedGroup(parsedData);
      });
    });

    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
    }

    return () => {
      newSockets.forEach((socket) => {
        if (socket.connected) {
          socket.disconnect();
        }
      });
    };
  }, [teamId, tasksData]);

  useEffect(() => {
    // Initialize state for each task
    const initialElapsedTimes = tasksData.map(() => 0);
    const initialIsRunning = tasksData.map(() => false);
    setElapsedTimes(initialElapsedTimes);
    setIsRunning(initialIsRunning);

    return () => {
      setElapsedTimes([]);
      setIsRunning([]);
    };
  }, [tasksData]);

  useEffect(() => {
    // Clear interval for each task when component unmounts
    return () => {
      elapsedTimes.forEach((_, index) => clearInterval(index));
    };
  }, [elapsedTimes]);

  const startTimer = (taskIndex) => {
    setIsRunning((prevIsRunning) => prevIsRunning.map((state, index) => (index === taskIndex ? true : state)));
  };

  const stopTimer = async (taskIndex) => {
    setIsRunning((prevIsRunning) => prevIsRunning.map((state, index) => (index === taskIndex ? false : state)));
    
    const finishedTime = formatTime(elapsedTimes[taskIndex]);
    console.log(`Finished Time for Task ${taskIndex + 1}:`, finishedTime);
    
    const finishedTimeData = {
      teamId,
      finishedTime,
      era: selectedGroup.groupInfo,
    };

    console.log(`Sending finished time for Task ${taskIndex + 1} to server:`, finishedTimeData);

    if (sockets[taskIndex]) {
      console.log(`Socket for Task ${taskIndex + 1} is connected. Emitting finishedTime event...`);
      sockets[taskIndex].emit('finishedTime', JSON.stringify(finishedTimeData));
    }

    if (selectedGroup) {
      try {
        const response = await fetch('http://localhost:3001/api/aikataulu', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            era: selectedGroup.groupInfo,
            joukkueId: finishedTimeData.teamId,
            aika: elapsedTimes[taskIndex],
          }),
        });

        if (!response.ok) {
          console.error(`Failed to send finished time for Task ${taskIndex + 1} to aikataulu:`, response.status);
        }
      } catch (error) {
        console.error(`Error sending finished time for Task ${taskIndex + 1} to aikataulu:`, error);
      }
    }
  };

  const resetTimer = (taskIndex) => {
    setElapsedTimes((prevElapsedTimes) => prevElapsedTimes.map((time, index) => (index === taskIndex ? 0 : time)));
    setIsRunning((prevIsRunning) => prevIsRunning.map((state, index) => (index === taskIndex ? false : state)));
  };

  const formatTime = (timeInMilliseconds) => {
    const minutes = Math.floor(timeInMilliseconds / (60 * 100));
    const seconds = Math.floor((timeInMilliseconds % (60 * 100)) / 100);
    const milliseconds = timeInMilliseconds % 100;

    return `${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}:${milliseconds < 10 ? '0' : ''}${milliseconds}`;
  };

  return (
    <div>
      <h1>Team {teamId} Stopwatch</h1>
      {selectedGroup && (
        <div>
          <h2>Selected Task: {selectedGroup.selectedTaskNames[selectedTask]}</h2>
          <p>Max Aika: {formatTime(selectedGroup.selectedTasks[selectedTask].max_aika * 100)}</p>
        </div>
      )}
      {tasksData.map((task, index) => (
        <div key={index} className="task-timer">
          <div className="timer">
            <span>{formatTime(elapsedTimes[index])}</span>
          </div>
          <div className="controls">
            <button onClick={() => startTimer(index)}>Start</button>
            <button onClick={() => stopTimer(index)}>Stop</button>
            <button onClick={() => resetTimer(index)}>Reset</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Ajastin;
