import React, { useState, useEffect } from 'react';

const AlkuEräLopetus = ({ eraName }) => {
  const [times, setTimes] = useState([]);
  const [allWinners, setAllWinners] = useState([]);
  const [remainingNonWinners, setRemainingNonWinners] = useState([]);

  const fetchTimes = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/aikataulu');
      if (!response.ok) {
        throw new Error('Failed to fetch times');
      }

      const data = await response.json();
      setTimes(data);

      return data;
    } catch (error) {
      console.error('Error fetching times:', error);
      throw error;
    }
  };

  useEffect(() => {
    const handleUpdate = async () => {
      try {
        const allWinnersUpdates = await Promise.all(allWinners.map(async (winner) => {
          const randomValue = generateRandomNumber();

          const response = await fetch('http://localhost:3001/api/updateJoukkueet', {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              updates: [{
                joukkueId: winner.teamId,
                updatedFields: {
                  semifinalist: randomValue
                },
              }],
            }),
          });

          if (!response.ok) {
            throw new Error('Failed to update data');
          }

          const data = await response.json();
          return {
            joukkueId: winner.teamId,
            updatedFields: data.updateResults.find(result => result.joukkueId === winner.teamId)?.updatedFields,
          };
        }));

        const remainingNonWinnersUpdates = await Promise.all(remainingNonWinners.map(async (nonWinner) => {
          const randomValue = generateRandomNumber();

          const response = await fetch('http://localhost:3001/api/updateJoukkueet', {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              updates: [{
                joukkueId: nonWinner.teamId,
                updatedFields: {
                  kerailyera: randomValue,
                },
              }],
            }),
          });

          if (!response.ok) {
            throw new Error('Failed to update data');
          }

          const data = await response.json();
          return {
            joukkueId: nonWinner.teamId,
            updatedFields: data.updateResults.find(result => result.joukkueId === nonWinner.teamId)?.updatedFields,
          };
        }));

        const response = await fetch('http://localhost:3001/api/updateJoukkueet', {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            updates: allWinnersUpdates.concat(remainingNonWinnersUpdates),
          }),
        });

        if (!response.ok) {
          throw new Error('Failed to update winners and non-winners');
        }

        const data = await response.json();

        console.log('Update Results:', data.updateResults);
      } catch (error) {
        console.error('Error handling lopeta click:', error);
      }
    };

    if (allWinners.length > 0) {
      handleUpdate();
    }
  }, [allWinners, remainingNonWinners]);

  const handleLopetaClick = async () => {
    try {
      const fetchedTimes = await fetchTimes();
      const { winnersByRound, allNonWinners } = findWinnersAndNonWinnersByRound(fetchedTimes);

      const removedTeams = allNonWinners.slice(0, 6);
      const remainingNonWinners = allNonWinners.slice(6);

      const allWinners = Object.values(winnersByRound)
        .reduce((acc, roundWinners) => acc.concat(roundWinners), [])
        .concat(removedTeams);

      setAllWinners(allWinners);
      setRemainingNonWinners(remainingNonWinners);

      console.log('All Winners:', allWinners);
      console.log('All Non-winners:', remainingNonWinners);
    } catch (error) {
      console.error('Error handling lopeta click:', error);
    }
  };

  const generateRandomNumber = () => {
    return Math.floor(Math.random() * 1000);
  };

  return (
    <div>
      <button onClick={handleLopetaClick}>Lopeta erä</button>
    </div>
  );
};

export default AlkuEräLopetus;
