import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TasksEdit from './TasksEdit'; // Create the TehtavaEdit component for task editing
import TasksRegister from './TasksRegister'; // Create the TasksRegister component for task registration
import TasksDisplay from './TasksDisplay'; // Create the TasksDisplay component for displaying individual tasks

function Tasks() {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  function fetchTasks() {
    axios
      .get('http://localhost:3001/api/tehtavat')
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });
  }

  useEffect(() => {
    fetchTasks();
  }, []);

  const handleEditTask = (task) => {
    setSelectedTask(task);
    setIsEditing(true);
    console.log('Selected Task:', task);
  };

  const handleExitEdit = () => {
    resetEditing();
  };

  const resetEditing = () => {
    setSelectedTask(null);
    setIsEditing(false);
  };

  const handleUpdateTask = (updatedTask) => {
    axios
      .put(`http://localhost:3001/api/tehtavat/${updatedTask.id}`, updatedTask)
      .then((response) => {
        console.log('Task updated:', response.data);
        setIsEditing(false);
        fetchTasks();
      })
      .catch((error) => {
        console.error('Error updating task:', error);
      });
  };

  const handleDeleteTask = (task) => {
    if (task.tehtävä_id) {
      axios
        .delete(`http://localhost:3001/api/tehtavat/${task.tehtävä_id}`)
        .then((response) => {
          console.log('Task deleted:', response.data);
          fetchTasks(); // Refetch the tasks
        })
        .catch((error) => {
          console.error('Error deleting task:', error);
        });
    } else {
      // The task doesn't have a valid ID, so it's likely a new task that hasn't been saved yet
      // Remove it from the local state without making a DELETE request
      setTasks((prevTasks) => prevTasks.filter((t) => t !== task));
    }
  };
  

  

  return (
    <div>
      <div className="form-container">
        <TasksRegister setTasks={setTasks} /> {/* Change "setTeams" to "setTasks" */}
        {isEditing && (
          <TasksEdit
            task={selectedTask} // Change "team" to "task"
            onUpdate={handleUpdateTask}
            onExitEdit={handleExitEdit}
          />
        )}
      </div>
      <TasksDisplay
        tasks={tasks}
        onEditTask={handleEditTask}
        onDeleteTask={handleDeleteTask}
      />
    </div>
  );
}

export default Tasks;
