import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import Socket from './Socket'; // Adjust the import path
function Ajastin() {
  const { teamId } = useParams(); // Access the teamId route parameter
  const [elapsedTime, setElapsedTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [selectedGroup, setSelectedGroup] = useState(null);

  useEffect(() => {
    socket.on('timerUpdate', (timerData) => {
      console.log(`Received timer update from a client: ${JSON.stringify(timerData)}`);
      // Handle the timer data
    });

    socket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    socket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    socket.on('selectedGroupUpdate', (updatedGroupData) => {
      console.log('Received updated group data:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);

      localStorage.setItem('selectedGroup', updatedGroupData);
    });

    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
    }

    return () => {
      // Clean up event listeners
      socket.off('timerUpdate');
    };
  }, []);


  useEffect(() => {
    let timer;
    if (isRunning) {
      timer = setInterval(() => {
        setElapsedTime((prevElapsedTime) => {
          const newElapsedTime = prevElapsedTime + 1;

          // Emit the updated time to the server
          const timeData = {
            teamId,
            elapsedTime: newElapsedTime,
          };

          socket.emit('timerUpdate', JSON.stringify(timeData));

          return newElapsedTime;
        });
      }, 10);
    }

    return () => {
      clearInterval(timer);
    };
  }, [isRunning]);

  useEffect(() => {
    if (selectedGroup && elapsedTime >= selectedGroup.selectedTask.max_aika * 100) {
      // Stop the timer if it reaches the maximum time
      stopTimer();
    }
  }, [elapsedTime, selectedGroup]);

  const socket = io('http://localhost:8000'); // Move socket instantiation outside the component to avoid creating multiple sockets

  const startTimer = () => {
    setIsRunning(true);
  };

  const stopTimer = () => {
    setIsRunning(false);
  };

  const resetTimer = () => {
    setIsRunning(false);
    setElapsedTime(0);
  };

  const formatTime = (timeInMilliseconds) => {
    const minutes = Math.floor(timeInMilliseconds / (60 * 100));
    const seconds = Math.floor((timeInMilliseconds % (60 * 100)) / 100);
    const milliseconds = timeInMilliseconds % 100;

    return `${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}:${milliseconds < 10 ? '0' : ''}${milliseconds}`;
  };

  return (
    <div>
      <h2>{`Team ${teamId} Stopwatch`}</h2>
      {selectedGroup && (
        <div>
          <h3>{`Selected Task: ${selectedGroup.selectedTask.tehtävä_nimi}`}</h3>
          <p>Max Aika: {formatTime(selectedGroup.selectedTask.max_aika * 100)}</p>
        </div>
      )}
      <div className="timer">
        <span>{formatTime(elapsedTime)}</span>
      </div>
      <div className="controls">
        <button onClick={startTimer}>Start</button>
        <button onClick={stopTimer}>Stop</button>
        <button onClick={resetTimer}>Reset</button>
      </div>
    </div>
  );
}

export default Ajastin;
