import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TasksSelector({ onSelectTasks }) {
  const [tasks, setTasks] = useState([]);
  const [selectedTaskNames, setSelectedTaskNames] = useState([null, null, null]);

  useEffect(() => {
    axios
      .get('http://localhost:3001/api/tehtavat')
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching tasks:', error);
      });

    const storedSelectedTaskIds = localStorage.getItem('selectedTaskIds');
    if (storedSelectedTaskIds) {
      setSelectedTaskNames(JSON.parse(storedSelectedTaskIds));
    }
  }, []);

  const handleTaskChange = (e, index) => {
    const taskName = e.target.value;
    const updatedTaskNames = [...selectedTaskNames];
    updatedTaskNames[index] = taskName;
    setSelectedTaskNames(updatedTaskNames);

    onSelectTasks(updatedTaskNames);
  };

  return (
    <div>
      <h2>Task Selector</h2>
      {selectedTaskNames.map((selectedTaskName, index) => (
        <select key={index} value={selectedTaskName} onChange={(e) => handleTaskChange(e, index)}>
          <option key="default" value="">
            Select a task
          </option>
          {tasks.map((task) => (
            <option key={task.id} value={task.tehtävä_nimi}>
              {task.tehtävä_nimi}
            </option>
          ))}
        </select>
      ))}
    </div>
  );
}

export default TasksSelector;
