import React, { useState } from 'react';
import axios from 'axios';

function TasksRegister({ onTaskCreated }) {
  const [formData, setFormData] = useState({
    tehtävä_nimi: '',
    max_aika: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post('http://localhost:3001/api/teht', formData)
      .then((response) => {
        console.log('Task created:', response.data);

        // Notify the parent component (Tasks.js) that a task has been created
        onTaskCreated(response.data);

        setFormData({
          tehtävä_nimi: '',
          max_aika: '',
        });
      })
      .catch((error) => {
        console.error('Error creating task:', error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div className="tasks-register-container">
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <h2>Task Registration</h2>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              id="tehtävä_nimi"
              name="tehtävä_nimi"
              placeholder="Task Name"
              value={formData.tehtävä_nimi}
              onChange={handleInputChange}
              required
              autoComplete="off"
            />
            <label htmlFor="tehtävä_nimi" className="form__label">Task Name</label>
          </div>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              id="max_aika"
              name="max_aika"
              placeholder="Max Time"
              value={formData.max_aika}
              onChange={handleInputChange}
              required
              autoComplete="off"
            />
            <label htmlFor="max_aika" className="form__label">Max Time</label>
          </div>
        </div>
        <button type="submit" className="submit-button">Submit</button>
      </form>
    </div>
  );
}

export default TasksRegister;
