import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';

function Ajastin() {
  const { teamId } = useParams();
  const [elapsedTimes, setElapsedTimes] = useState({});
  const [isRunning, setIsRunning] = useState(false);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    document.title = `Team ${teamId}`;
    const newSocket = io('http://localhost:8000');

    newSocket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    newSocket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    newSocket.on('selectedGroupUpdate', (updatedGroupData) => {
      console.log('Received updated group data:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);

      localStorage.setItem('selectedGroup', updatedGroupData);
    });

    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
    }

    setSocket(newSocket);

    return () => {
      if (newSocket.connected) {
        newSocket.disconnect();
      }
    };
  }, [teamId]);

  useEffect(() => {
    const timers = {};
    selectedGroup?.selectedTaskNames.forEach((taskName) => {
      timers[taskName] = {
        elapsedTime: 0,
        intervalId: null,
      };
    });
    setElapsedTimes(timers);

    return () => {
      Object.values(timers).forEach((timer) => clearInterval(timer.intervalId));
    };
  }, [selectedGroup]);

  useEffect(() => {
    Object.values(elapsedTimes).forEach((timer) => {
      if (isRunning) {
        timer.intervalId = setInterval(() => {
          setElapsedTimes((prevElapsedTimes) => ({
            ...prevElapsedTimes,
            [timer.taskName]: {
              ...prevElapsedTimes[timer.taskName],
              elapsedTime: prevElapsedTimes[timer.taskName].elapsedTime + 1,
            },
          }));
        }, 10);
      } else {
        clearInterval(timer.intervalId);
      }
    });

    return () => {
      Object.values(elapsedTimes).forEach((timer) => clearInterval(timer.intervalId));
    };
  }, [isRunning, elapsedTimes]);

  const startTimer = () => {
    setIsRunning(true);
  };

  const stopTimer = () => {
    setIsRunning(false);
  };

  const resetTimer = () => {
    const newElapsedTimes = {};
    Object.keys(elapsedTimes).forEach((taskName) => {
      newElapsedTimes[taskName] = {
        elapsedTime: 0,
        intervalId: null,
      };
    });
    setElapsedTimes(newElapsedTimes);
    setIsRunning(false);
  };

  function formatTime(timeInMilliseconds) {
    const minutes = Math.floor(timeInMilliseconds / (60 * 100));
    const seconds = Math.floor((timeInMilliseconds % (60 * 100)) / 100);
    const milliseconds = timeInMilliseconds % 100;
  
    return `${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}:${milliseconds < 10 ? '0' : ''}${milliseconds}`;
  }
  

  return (
    <div>
      <h2>{`Team ${teamId} Stopwatch`}</h2>
      <div className="timer-container">
        {selectedGroup &&
          selectedGroup.selectedTaskNames.map((taskName) => (
            <div key={taskName} className="timer">
              <span>{`${taskName}: ${formatTime(elapsedTimes[taskName]?.elapsedTime * 10)}`}</span>
            </div>
          ))}
      </div>
      <div className="controls">
        <button onClick={startTimer}>Start</button>
        <button onClick={stopTimer}>Stop</button>
        <button onClick={resetTimer}>Reset</button>
      </div>
    </div>
  );
}

export default Ajastin;
