import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';

function Ajastin() {
  const { teamId } = useParams();
  const [elapsedTimes, setElapsedTimes] = useState({});
  const [isRunning, setIsRunning] = useState(false);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    document.title = `Team ${teamId}`;
    const newSocket = io('http://localhost:8000');

    newSocket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });

    newSocket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });

    newSocket.on('selectedGroupUpdate', (updatedGroupData) => {
      console.log('Received updated group data:', updatedGroupData);
      const parsedData = JSON.parse(updatedGroupData);
      setSelectedGroup(parsedData);

      localStorage.setItem('selectedGroup', updatedGroupData);
    });

    const storedSelectedGroup = localStorage.getItem('selectedGroup');
    if (storedSelectedGroup) {
      const parsedData = JSON.parse(storedSelectedGroup);
      setSelectedGroup(parsedData);
    }

    setSocket(newSocket);

    return () => {
      if (newSocket.connected) {
        newSocket.disconnect();
      }
    };
  }, [teamId]);

  useEffect(() => {
    let timers = {};
    if (isRunning) {
      timers = setInterval(() => {
        setElapsedTimes((prevElapsedTimes) => {
          const updatedElapsedTimes = {};
          for (const taskId in prevElapsedTimes) {
            updatedElapsedTimes[taskId] = prevElapsedTimes[taskId] + 1;
          }
          return updatedElapsedTimes;
        });
      }, 10);
    }

    return () => {
      clearInterval(timers);
    };
  }, [isRunning]);

  useEffect(() => {
    if (selectedGroup) {
      const initialElapsedTimes = {};
      for (const task of selectedGroup.selectedTasks) {
        initialElapsedTimes[task.id] = 0;
      }
      setElapsedTimes(initialElapsedTimes);
    }
  }, [selectedGroup]);

  const startTimer = () => {
    setIsRunning(true);
  };

  const stopTimer = async (taskId) => {
    setIsRunning(false);
    const finishedTime = formatTime(elapsedTimes[taskId]);
    console.log(`Elapsed Time for Task ${taskId}:`, elapsedTimes[taskId]);
    console.log(`Finished Time for Task ${taskId}:`, finishedTime);
    const finishedTimeData = {
      teamId,
      finishedTime,
      era: selectedGroup.groupInfo,
      taskId,
    };
    console.log(`Sending finished time for Task ${taskId} to server:`, finishedTimeData);
    if (socket) {
      console.log('Socket is connected. Emitting finishedTime event...');
      socket.emit('finishedTime', JSON.stringify(finishedTimeData));
    }

    if (selectedGroup) {
      try {
        const response = await fetch('http://localhost:3001/api/aikataulu', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            era: selectedGroup.groupInfo,
            joukkueId: finishedTimeData.teamId,
            aika: elapsedTimes[taskId],
            taskId: finishedTimeData.taskId,
          }),
        });

        if (!response.ok) {
          console.error(`Failed to send finished time for Task ${taskId} to aikataulu:`, response.status);
        }
      } catch (error) {
        console.error(`Error sending finished time for Task ${taskId} to aikataulu:`, error);
      }
    }
  };

  const resetTimer = () => {
    setIsRunning(false);
    const initialElapsedTimes = {};
    for (const taskId in elapsedTimes) {
      initialElapsedTimes[taskId] = 0;
    }
    setElapsedTimes(initialElapsedTimes);
  };

  const formatTime = (timeInMilliseconds) => {
    const minutes = Math.floor(timeInMilliseconds / (60 * 100));
    const seconds = Math.floor((timeInMilliseconds % (60 * 100)) / 100);
    const milliseconds = timeInMilliseconds % 100;

    return `${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}:${milliseconds < 10 ? '0' : ''}${milliseconds}`;
  };

  return (
    <div>
      <h2>{`Team ${teamId} Stopwatch`}</h2>
      {selectedGroup && (
        <div>
          {selectedGroup.selectedTasks.map((task) => (
            <div key={task.id}>
              <h3>{`Selected Task: ${task.tehtävä_nimi}`}</h3>
              <p>Max Aika: {formatTime(task.max_aika * 100)}</p>
              <div className="timer">
                <span>{formatTime(elapsedTimes[task.id])}</span>
              </div>
              <div className="controls">
                <button onClick={() => startTimer(task.id)}>Start</button>
                <button onClick={() => stopTimer(task.id)}>Stop</button>
                <button onClick={resetTimer}>Reset</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Ajastin;
