import React, { useState } from 'react';
import axios from 'axios';

function TasksEdit({ setTehtävät }) {
    const [formData, setFormData] = useState({
        tehtävä_nimi: task.tehtävä_nimi || '', // Initialize with task data or empty string
        max_aika: task.max_aika || '', // Initialize with task data or empty string
      });
      
      // Use an effect to update the form data when a new task is selected
      useEffect(() => {
        setFormData({
          tehtävä_nimi: task.tehtävä_nimi || '',
          max_aika: task.max_aika || '',
        });
      }, [task]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post('http://localhost:3001/api/tehtävät', formData) // Change the URL and endpoint to tasks
      .then((response) => {
        console.log('Task created:', response.data);
        setFormData({
          tehtävä_nimi: '',
          max_aika: '',
        });
        setTehtävät((prevTasks) => [...prevTasks, response.data]);
      })
      .catch((error) => {
        console.error('Error creating task:', error);
      });
  };

  return (
    <div className="register-container">
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <h2>Register Task</h2>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="tehtävä_nimi"
              placeholder="Task Name"
              value={formData.tehtävä_nimi}
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Task Name</label>
          </div>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="max_aika"
              placeholder="Max Time"
              value={formData.max_aika}
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Max Time</label>
          </div>
        </div>
        <button type="submit" className="submit-button">
          Register
        </button>
      </form>
    </div>
  );
}

export default TasksEdit;
