import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Competition() {
  const [selectedGroup, setSelectedGroup] = useState(null);

  useEffect(() => {
    axios
      .get('http://localhost:8000/api/selectedGroups')
      .then((response) => {
        setSelectedGroup(response.data); // Response should be the group data
      })
      .catch((error) => {
        console.error('Error fetching selected group:', error);
      });
  }, []);

  const renderGroupData = () => {
    if (selectedGroup) {
      return (
        <div>
          <h2>Group {selectedGroup.joukkue_id}</h2>
          <p>Team Name: {selectedGroup.joukkue_nimi}</p>
          <p>Members: {selectedGroup.jäsenet}</p>
          <p>City: {selectedGroup.kaupunki}</p>
          <p>Participation Number: {selectedGroup.osallistumis_nro}</p>
          <p>Semifinalist: {selectedGroup.semifinalist}</p>
          <p>Finalist: {selectedGroup.finalist}</p>
        </div>
      );
    } else {
      return <p>No group selected</p>;
    }
  };

  return (
    <div>
      <h1>Competition</h1>
      {renderGroupData()}
    </div>
  );
}

export default Competition;
