import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

function Competition() {
  const [selectedGroup, setSelectedGroup] = useState(null);

  useEffect(() => {
    const socket = io('http://localhost:8000');

    socket.on('connect', () => {
      console.log('WebSocket connection is open.');
    });
    
    socket.on('selectedGroup', (groupData) => {
      console.log('Received live selected group:', groupData);
      setSelectedGroup(groupData);
    });
    
    socket.on('disconnect', () => {
      console.log('WebSocket connection is closed.');
    });
    

    return () => {
      if (socket.connected) {
        socket.disconnect();
      }
    };
  }, []);

  return (
    <div>
      <h1>Competition</h1>
      {selectedGroup ? (
        <div>
          <h2>Group {selectedGroup.joukkue_id}</h2>
          <p>Team Name: {selectedGroup.joukkue_nimi}</p>
          <p>Members: {selectedGroup.jäsenet}</p>
          <p>City: {selectedGroup.kaupunki}</p>
          <p>Participation Number: {selectedGroup.osallistumis_nro}</p>
          <p>Semifinalist: {selectedGroup.semifinalist}</p>
          <p>Finalist: {selectedGroup.finalist}</p>
        </div>
      ) : (
        <p>No selected group available.</p>
      )}
    </div>
  );
}

export default Competition;
