import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Competition() {
  const [selectedGroups, setSelectedGroups] = useState([]);

  useEffect(() => {
    axios
      .get('http://localhost:8000/api/selectedGroups')
      .then((response) => {
        setSelectedGroups(response.data);
      })
      .catch((error) => {
        console.error('Error fetching selected groups:', error);
      });
  }, []);

  return (
    <div>
      <h1>Competition</h1>
      <ul>
      {selectedGroups.map((group) => (
  <li key={group.joukkue_id}>
    <h2>Group {group.joukkue_id}</h2>
    <p>Team Name: {group.joukkue_nimi}</p>
    <p>Members: {group.jäsenet}</p>
    <p>City: {group.kaupunki}</p>
    <p>Participation Number: {group.osallistumis_nro}</p>
    <p>Semifinalist: {group.semifinalist}</p>
    <p>Finalist: {group.finalist}</p>
  </li>
))}

      </ul>
    </div>
  );
}

export default Competition;
