import React, { useState } from 'react';
import '../css/TasksEdit.css'; // Adjust the CSS file as needed

function TasksRegister({ setTehtävät }) {
  const [formData, setFormData] = useState({
    tehtävä_nimi: '',
    max_aika: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Send the formData to the server and handle task registration
    // Adjust the URL and API endpoint accordingly
    axios
      .post('http://localhost:3001/api/tehtävät', formData) // Change the URL and endpoint to tasks
      .then((response) => {
        console.log('Task created:', response.data);
        setFormData({
          tehtävä_nimi: '',
          max_aika: '',
        });
        setTehtävät((prevTasks) => [...prevTasks, response.data]);
      })
      .catch((error) => {
        console.error('Error creating task:', error);
      });
  };

  return (
    <div className="register-container">
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <h2>Register Task</h2>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="tehtävä_nimi"
              placeholder="Task Name"
              value={formData.tehtävä_nimi}
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Task Name</label>
          </div>
          <div className="form__group">
            <input
              type="text"
              className="form__field"
              name="max_aika"
              placeholder="Max Time"
              value={formData.max_aika}
              onChange={handleInputChange}
              required
            />
            <label className="form__label">Max Time</label>
          </div>
        </div>
        <button type="submit" className="submit-button">
          Register
        </button>
      </form>
    </div>
  );
}

export default TasksEdit;
