import React, { useState, useEffect } from 'react';

const AlkuEräLopetus = ({ eraName }) => {
  const [times, setTimes] = useState([]);

  useEffect(() => {
    fetchTimes();
  }, []);

  const fetchTimes = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/aikataulu');
      if (!response.ok) {
        throw new Error('Failed to fetch times');
      }

      const data = await response.json();
      setTimes(data);
    } catch (error) {
      console.error('Error fetching times:', error);
    }
  };

  const groupTimesByEra = (times, eraName) => {
    const filteredTimes = times.filter((time) => time.era.includes(eraName));
  
    const groupedTimes = {};
    filteredTimes.forEach((time) => {
      const eraNumber = time.era.match(/\d+/);
      if (eraNumber) {
        const key = `Era ${eraNumber[0]}`;
        if (!groupedTimes[key]) {
          groupedTimes[key] = [];
        }
        groupedTimes[key].push(time);
      }
    });
  
    return groupedTimes;
  };
  

  const handleLopetaClick = async () => {
    try {
      await fetchTimes();
  
      if (times && times.length > 0) {
        const groupedByEra = groupTimesByEra(times, 'Alkuerä');
        console.log('Grouped by era:', groupedByEra);
  
        const winners = {};
        Object.keys(groupedByEra).forEach((eraKey) => {
          const group = groupedByEra[eraKey];
          const winner = findWinner(group);
          winners[eraKey] = winner;
        });
  
        console.log('Winners:', winners);
  
        const nonWinners = [];
        Object.keys(groupedByEra).forEach((eraKey) => {
          const group = groupedByEra[eraKey];
          const winner = findWinner(group);
          const nonWinnerGroup = group.filter((time) => !isSameTime(time, winner));
          nonWinners.push(...nonWinnerGroup);
        });
  
        console.log('Non-winners:', nonWinners);
  
        const sortedNonWinners = nonWinners.sort((a, b) => a.aika - b.aika);
        const sixBestNonWinners = sortedNonWinners.slice(0, 6);
  
        console.log('Six best non-winners:', sixBestNonWinners);
  
        const combinedWinners = Object.values(winners).concat(sixBestNonWinners);
        console.log('Combined winners:', combinedWinners);
  
        // Separate times into two lists
        const combinedWinnersList = combinedWinners;
        const nonWinnersList = nonWinners.filter(
          (time) => !combinedWinners.some((combinedWinner) => isSameTime(time, combinedWinner))
        );
  
        console.log('Combined Winners List:', combinedWinnersList);
        console.log('Non-winners List:', nonWinnersList);
  
        // Update the kerailyera field for teams in the non-winners list
        const updatePromises = nonWinnersList.map((nonWinner) => {
          const joukkueId = nonWinner.joukkue_id;
          return fetch(`http://localhost:3001/api/updateJoukkueet/updateKerailyera/${joukkueId}`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
            },
          });
        });
  
        const updateResults = await Promise.all(updatePromises);
        console.log('Update results:', updateResults);
      } else {
        alert('No times available.');
      }
    } catch (error) {
      console.error('Error handling lopeta click:', error);
    }
  };
  
  
  
  
  
  const isSameTime = (time1, time2) => {
    return time1.aika === time2.aika;
  };
  
  const findWinner = (group) => {
    return group.reduce((winner, current) => {
      return Number(current.aika) < Number(winner.aika) ? current : winner;
    });
  };
  
  
  
  
console.log(times)
  return (
    <div>
      <button onClick={handleLopetaClick}>Lopeta erä</button>
    </div>
  );
};

export default AlkuEräLopetus;
